import React, { useState } from 'react';
import { Link as RouterLink, useLocation } from 'react-router-dom';
import {
  AppBar,
  Box,
  Toolbar,
  IconButton,
  Typography,
  Menu,
  Container,
  Button,
  MenuItem,
  useMediaQuery,
  useTheme,
  Badge,
  Tooltip,
  Divider,
  InputBase,
  alpha,
} from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';
import SearchIcon from '@mui/icons-material/Search';
import DownloadIcon from '@mui/icons-material/Download';
import NotificationsIcon from '@mui/icons-material/Notifications';
import CloudDownloadIcon from '@mui/icons-material/CloudDownload';
import MusicNoteIcon from '@mui/icons-material/MusicNote';
import InfoIcon from '@mui/icons-material/Info';
import HomeIcon from '@mui/icons-material/Home';
import PhotoLibraryIcon from '@mui/icons-material/PhotoLibrary';
import PeopleIcon from '@mui/icons-material/People';

const pages = [
  { title: 'Home', path: '/', icon: <HomeIcon fontSize="small" sx={{ mr: 0.5 }} /> },
  { title: 'Episodes', path: '/episodes', icon: <CloudDownloadIcon fontSize="small" sx={{ mr: 0.5 }} /> },
  { title: 'Soundtracks', path: '/soundtracks', icon: <MusicNoteIcon fontSize="small" sx={{ mr: 0.5 }} /> },
  { title: 'Gallery', path: '/gallery', icon: <PhotoLibraryIcon fontSize="small" sx={{ mr: 0.5 }} /> },
  { title: 'Characters', path: '/characters', icon: <PeopleIcon fontSize="small" sx={{ mr: 0.5 }} /> },
  { title: 'About', path: '/about', icon: <InfoIcon fontSize="small" sx={{ mr: 0.5 }} /> },
];

function Navbar() {
  const [anchorElNav, setAnchorElNav] = useState(null);
  const location = useLocation();
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));

  const handleOpenNavMenu = (event) => {
    setAnchorElNav(event.currentTarget);
  };

  const handleCloseNavMenu = () => {
    setAnchorElNav(null);
  };

  return (
    <AppBar 
      position="sticky" 
      sx={{ 
        backgroundColor: '#1a237e',
        backgroundImage: 'linear-gradient(90deg, #1a237e 0%, #3949ab 100%)',
        boxShadow: '0 2px 10px rgba(0,0,0,0.2)',
      }}
    >
      <Container maxWidth="xl">
        <Toolbar disableGutters>
          {/* Logo for larger screens */}
          <Typography
            variant="h5"
            noWrap
            component={RouterLink}
            to="/"
            sx={{
              mr: 2,
              display: { xs: 'none', md: 'flex' },
              fontFamily: 'monospace',
              fontWeight: 700,
              letterSpacing: '.1rem',
              color: 'white',
              textDecoration: 'none',
              alignItems: 'center',
            }}
          >
            <MusicNoteIcon sx={{ mr: 1 }} />
            Your Lie in <span style={{ color: '#ffb6c1', fontWeight: 600 }}>April</span>
          </Typography>

          {/* Mobile menu */}
          <Box sx={{ flexGrow: 1, display: { xs: 'flex', md: 'none' } }}>
            <IconButton
              size="large"
              aria-label="menu"
              aria-controls="menu-appbar"
              aria-haspopup="true"
              onClick={handleOpenNavMenu}
              color="inherit"
            >
              <MenuIcon />
            </IconButton>
            <Menu
              id="menu-appbar"
              anchorEl={anchorElNav}
              anchorOrigin={{
                vertical: 'bottom',
                horizontal: 'left',
              }}
              keepMounted
              transformOrigin={{
                vertical: 'top',
                horizontal: 'left',
              }}
              open={Boolean(anchorElNav)}
              onClose={handleCloseNavMenu}
              sx={{
                display: { xs: 'block', md: 'none' },
              }}
            >
              {pages.map((page) => (
                <MenuItem 
                  key={page.title} 
                  onClick={handleCloseNavMenu}
                  component={RouterLink}
                  to={page.path}
                  selected={location.pathname === page.path}
                  sx={{
                    '&.Mui-selected': {
                      backgroundColor: alpha(theme.palette.primary.main, 0.1),
                    }
                  }}
                >
                  <Box sx={{ display: 'flex', alignItems: 'center' }}>
                    {page.icon}
                    <Typography textAlign="center">{page.title}</Typography>
                  </Box>
                </MenuItem>
              ))}
              <Divider />
              <MenuItem 
                onClick={handleCloseNavMenu}
                component={RouterLink}
                to="/contact"
              >
                <Typography textAlign="center">Contact Us</Typography>
              </MenuItem>
            </Menu>
          </Box>

          {/* Logo for mobile screens */}
          <Typography
            variant="h6"
            noWrap
            component={RouterLink}
            to="/"
            sx={{
              mr: 2,
              display: { xs: 'flex', md: 'none' },
              flexGrow: 1,
              fontFamily: 'monospace',
              fontWeight: 700,
              color: 'white',
              textDecoration: 'none',
              alignItems: 'center',
            }}
          >
            <MusicNoteIcon sx={{ mr: 0.5 }} />
            Your Lie in April
          </Typography>

          {/* Desktop menu */}
          <Box sx={{ flexGrow: 1, display: { xs: 'none', md: 'flex' }, justifyContent: 'center' }}>
            {pages.map((page) => (
              <Button
                key={page.title}
                component={RouterLink}
                to={page.path}
                onClick={handleCloseNavMenu}
                sx={{ 
                  color: 'white', 
                  display: 'flex',
                  alignItems: 'center',
                  mx: 1,
                  px: 2,
                  py: 1,
                  borderRadius: 1,
                  position: 'relative',
                  '&:after': location.pathname === page.path ? {
                    content: '""',
                    position: 'absolute',
                    bottom: 0,
                    left: '50%',
                    transform: 'translateX(-50%)',
                    width: '50%',
                    height: '3px',
                    backgroundColor: '#ffb6c1',
                    borderRadius: '3px 3px 0 0',
                  } : {},
                  '&:hover': {
                    backgroundColor: 'rgba(255, 255, 255, 0.1)',
                  }
                }}
              >
                {page.icon}
                {page.title}
              </Button>
            ))}
          </Box>

          {/* Search bar - desktop only */}
          <Box 
            sx={{ 
              position: 'relative',
              borderRadius: 2,
              backgroundColor: alpha('#fff', 0.15),
              '&:hover': {
                backgroundColor: alpha('#fff', 0.25),
              },
              mr: 2,
              width: { xs: 'none', md: 'auto' },
              display: { xs: 'none', md: 'block' }
            }}
          >
            <Box sx={{ position: 'absolute', height: '100%', display: 'flex', alignItems: 'center', pl: 2 }}>
              <SearchIcon sx={{ color: 'white' }} />
            </Box>
            <InputBase
              placeholder="Search episodes..."
              sx={{
                color: 'white',
                pl: 5,
                pr: 1,
                py: 0.75,
                width: '100%',
                '& ::placeholder': {
                  color: 'rgba(255, 255, 255, 0.7)',
                  opacity: 1,
                },
              }}
            />
          </Box>

          {/* Right side icons */}
          <Box sx={{ display: 'flex', alignItems: 'center' }}>
            <Tooltip title="New episode available">
              <IconButton color="inherit" sx={{ mr: { xs: 1, md: 2 } }}>
                <Badge badgeContent={1} color="error">
                  <NotificationsIcon />
                </Badge>
              </IconButton>
            </Tooltip>
            
            <Button
              variant="contained"
              startIcon={<CloudDownloadIcon />}
              component={RouterLink}
              to="/episodes"
              sx={{ 
                display: { xs: 'none', md: 'flex' },
                borderRadius: 2,
                textTransform: 'none',
                px: 2,
                boxShadow: '0 2px 5px rgba(0,0,0,0.2)',
                bgcolor: '#ffb6c1',
                color: '#000',
                fontWeight: 'bold',
                '&:hover': {
                  bgcolor: '#ff99a8',
                }
              }}
            >
              Download All
            </Button>
          </Box>
        </Toolbar>
      </Container>
    </AppBar>
  );
}

export default Navbar;
