import React, { useState, useEffect } from 'react';
import { useParams, Link as RouterLink } from 'react-router-dom';
import {
  Box,
  Typography,
  Container,
  Grid,
  Paper,
  Button,
  Divider,
  List,
  ListItem,
  ListItemText,
  Card,
  CardMedia,
  CardContent,
  CardActions,
  Tabs,
  Tab,
  IconButton,
  Chip,
  Link,
  Stack,
  Avatar,
} from '@mui/material';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import ArrowForwardIcon from '@mui/icons-material/ArrowForward';
import FavoriteIcon from '@mui/icons-material/Favorite';
import ShareIcon from '@mui/icons-material/Share';
import DownloadIcon from '@mui/icons-material/Download';
import CloudDownloadIcon from '@mui/icons-material/CloudDownload';
import HighQualityIcon from '@mui/icons-material/HighQuality';
import SpeedIcon from '@mui/icons-material/Speed';
import StorageIcon from '@mui/icons-material/Storage';
import LockIcon from '@mui/icons-material/Lock';

// Mock data for episodes
const allEpisodes = [
  {
    id: 1,
    title: 'Episode 1: Monotone/Colorful',
    description: 'Kousei Arima was a piano prodigy until his mother died when he was eleven years old. After her death, Kousei became unable to hear the sound of his piano, and he stopped playing. Two years later, Kousei still avoids the piano and lives a colorless life alongside his childhood friends Tsubaki and Watari. However, everything changes when Kousei meets the beautiful violinist Kaori Miyazono, who stirs up his world and sets him on a journey to face music again.',
    image: 'https://m.media-amazon.com/images/M/MV5BYThlNWY5ZDgtYTIxNC00ZjdiLWJmNGUtMDFjMDlmZTAzOWFiXkEyXkFqcGdeQXVyNjAwNDUxODI@._V1_.jpg',
    videoUrl: 'https://example.com/episode1',
    season: 1,
    duration: '23 min',
    releaseDate: 'October 9, 2014',
    nextEpisode: 2,
    prevEpisode: null,
    relatedEpisodes: [2, 3, 4],
    totalEpisodes: 22,
    screenshots: [
      'https://m.media-amazon.com/images/M/MV5BYThlNWY5ZDgtYTIxNC00ZjdiLWJmNGUtMDFjMDlmZTAzOWFiXkEyXkFqcGdeQXVyNjAwNDUxODI@._V1_.jpg',
      'https://m.media-amazon.com/images/M/MV5BMTQ5MjExMzQtMzJlZS00NWU3LTg3NDItYTY5YzU4YzYwZTRjXkEyXkFqcGdeQXVyNjAwNDUxODI@._V1_.jpg',
      'https://m.media-amazon.com/images/M/MV5BNzg5MmI0ZDctODg1Ny00ZDgxLWI0MDgtYmNhZmFiYzM2MWZmXkEyXkFqcGdeQXVyNjAwNDUxODI@._V1_.jpg',
    ],
    downloadLinks: {
      '1080p': [
        { server: 'Server 1', link: 'https://example.com/download/1080p/1' },
        { server: 'Server 2', link: 'https://example.com/download/1080p/2' },
        { server: 'Server 3', link: 'https://example.com/download/1080p/3' },
      ],
      '720p': [
        { server: 'Server 1', link: 'https://example.com/download/720p/1' },
        { server: 'Server 2', link: 'https://example.com/download/720p/2' },
      ],
      '480p': [
        { server: 'Server 1', link: 'https://example.com/download/480p/1' },
      ],
    },
  },
  {
    id: 2,
    title: 'Episode 2: Friend A',
    description: 'Kaori forces Kousei to be her accompanist for a violin competition, but he refuses. Tsubaki and Watari convince him to at least watch Kaori perform. At the competition, Kaori plays with a unique, free-spirited style that captivates the audience but upsets the judges. After her performance, she confronts Kousei about becoming her accompanist.',
    image: 'https://m.media-amazon.com/images/M/MV5BMTQ5MjExMzQtMzJlZS00NWU3LTg3NDItYTY5YzU4YzYwZTRjXkEyXkFqcGdeQXVyNjAwNDUxODI@._V1_.jpg',
    videoUrl: 'https://example.com/episode2',
    season: 1,
    duration: '23 min',
    releaseDate: 'October 16, 2014',
    nextEpisode: 3,
    prevEpisode: 1,
    relatedEpisodes: [1, 3, 4],
  },
  {
    id: 3,
    title: 'Episode 3: Inside Spring',
    description: 'Kousei reluctantly agrees to be Kaori\'s accompanist, but his trauma prevents him from hearing the piano. As they practice together, Kaori tries to help Kousei overcome his fear. Meanwhile, Tsubaki begins to realize her feelings for Kousei might be more than just friendship.',
    image: 'https://m.media-amazon.com/images/M/MV5BNzg5MmI0ZDctODg1Ny00ZDgxLWI0MDgtYmNhZmFiYzM2MWZmXkEyXkFqcGdeQXVyNjAwNDUxODI@._V1_.jpg',
    videoUrl: 'https://example.com/episode3',
    season: 1,
    duration: '23 min',
    releaseDate: 'October 23, 2014',
    nextEpisode: 4,
    prevEpisode: 2,
    relatedEpisodes: [1, 2, 4],
  },
  {
    id: 4,
    title: 'Episode 4: The Journey',
    description: 'Kousei and Kaori perform at the competition, but Kousei freezes on stage. Despite his panic, Kaori\'s playing helps him find his way back to the music. Their unconventional performance leaves a lasting impression on the audience.',
    image: 'https://m.media-amazon.com/images/M/MV5BYThlNWY5ZDgtYTIxNC00ZjdiLWJmNGUtMDFjMDlmZTAzOWFiXkEyXkFqcGdeQXVyNjAwNDUxODI@._V1_.jpg',
    videoUrl: 'https://example.com/episode4',
    season: 1,
    duration: '23 min',
    releaseDate: 'October 30, 2014',
    nextEpisode: 5,
    prevEpisode: 3,
    relatedEpisodes: [1, 2, 3],
  },
];

function EpisodeDetailPage() {
  const { id } = useParams();
  const [episode, setEpisode] = useState(null);
  const [tabValue, setTabValue] = useState(0);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Simulate API call to fetch episode details
    const fetchEpisode = () => {
      setLoading(true);
      // Find episode by id
      const foundEpisode = allEpisodes.find((ep) => ep.id === parseInt(id));
      setEpisode(foundEpisode);
      setLoading(false);
    };

    fetchEpisode();
  }, [id]);

  const handleTabChange = (event, newValue) => {
    setTabValue(newValue);
  };

  if (loading || !episode) {
    return (
      <Container maxWidth="lg" sx={{ py: 4 }}>
        <Typography variant="h5">Loading episode...</Typography>
      </Container>
    );
  }

  // Get related episodes
  const relatedEpisodes = allEpisodes.filter((ep) => 
    episode.relatedEpisodes.includes(ep.id)
  );

  return (
    <Container maxWidth="lg" sx={{ py: 4 }}>
      <Button
        component={RouterLink}
        to="/episodes"
        startIcon={<ArrowBackIcon />}
        sx={{ mb: 3 }}
      >
        Back to Episodes
      </Button>

      <Box sx={{ 
        display: 'flex', 
        justifyContent: 'space-between', 
        alignItems: 'center', 
        mb: 3,
        background: 'linear-gradient(90deg, #1a237e 0%, #3949ab 100%)',
        p: 3,
        borderRadius: 2,
        color: 'white'
      }}>
        <Typography variant="h4" component="h1" gutterBottom sx={{ mb: 0 }}>
          {episode.title}
        </Typography>
        <Typography variant="h6" component="div" sx={{ 
          backgroundColor: 'rgba(255, 255, 255, 0.2)', 
          px: 2, 
          py: 1, 
          borderRadius: 1,
          fontWeight: 'bold'
        }}>
          Total Episodes: {episode.totalEpisodes}
        </Typography>
      </Box>

      <Grid container spacing={4}>
        <Grid item xs={12} md={8}>
          {/* Episode Banner */}
          <Paper
            sx={{
              position: 'relative',
              backgroundColor: 'black',
              mb: 3,
              pt: '56.25%', // 16:9 Aspect Ratio
              borderRadius: 2,
              overflow: 'hidden',
            }}
          >
            <Box
              sx={{
                position: 'absolute',
                top: 0,
                left: 0,
                width: '100%',
                height: '100%',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                backgroundImage: `url(${episode.image})`,
                backgroundSize: 'cover',
                backgroundPosition: 'center',
              }}
            >
              <Box
                sx={{
                  position: 'absolute',
                  top: 0,
                  left: 0,
                  width: '100%',
                  height: '100%',
                  backgroundColor: 'rgba(0,0,0,0.5)',
                }}
              />
              <Box sx={{ zIndex: 1, textAlign: 'center' }}>
                <Typography variant="h4" component="div" sx={{ color: 'white', mb: 2, fontWeight: 'bold' }}>
                  {episode.title}
                </Typography>
                <Button
                  variant="contained"
                  color="primary"
                  size="large"
                  startIcon={<DownloadIcon />}
                  sx={{ borderRadius: 4, px: 3, py: 1 }}
                >
                  Download Now
                </Button>
              </Box>
            </Box>
          </Paper>

          {/* Episode Navigation */}
          <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 3 }}>
            <Button
              variant="outlined"
              startIcon={<ArrowBackIcon />}
              component={RouterLink}
              to={episode.prevEpisode ? `/episodes/${episode.prevEpisode}` : '#'}
              disabled={!episode.prevEpisode}
              sx={{ borderRadius: 4 }}
            >
              Previous Episode
            </Button>
            <Button
              variant="outlined"
              endIcon={<ArrowForwardIcon />}
              component={RouterLink}
              to={episode.nextEpisode ? `/episodes/${episode.nextEpisode}` : '#'}
              disabled={!episode.nextEpisode}
              sx={{ borderRadius: 4 }}
            >
              Next Episode
            </Button>
          </Box>

          {/* Episode Info Tabs */}
          <Box sx={{ width: '100%', mb: 4 }}>
            <Box sx={{ borderBottom: 1, borderColor: 'divider' }}>
              <Tabs
                value={tabValue}
                onChange={handleTabChange}
                indicatorColor="secondary"
                textColor="inherit"
                variant="scrollable"
                scrollButtons="auto"
              >
                <Tab label="Description" />
                <Tab label="Download Links" />
                <Tab label="Screenshots" />
              </Tabs>
            </Box>
            <Box sx={{ p: 3, bgcolor: '#f5f5f5', borderRadius: '0 0 8px 8px' }}>
              {tabValue === 0 && (
                <Box>
                  <Typography variant="body1" paragraph>
                    {episode.description}
                  </Typography>
                  <Divider sx={{ my: 2 }} />
                  <Grid container spacing={2}>
                    <Grid item xs={6} md={3}>
                      <Typography variant="subtitle2" color="text.secondary">
                        Season
                      </Typography>
                      <Typography variant="body2">{episode.season}</Typography>
                    </Grid>
                    <Grid item xs={6} md={3}>
                      <Typography variant="subtitle2" color="text.secondary">
                        Duration
                      </Typography>
                      <Typography variant="body2">{episode.duration}</Typography>
                    </Grid>
                    <Grid item xs={6} md={3}>
                      <Typography variant="subtitle2" color="text.secondary">
                        Release Date
                      </Typography>
                      <Typography variant="body2">{episode.releaseDate}</Typography>
                    </Grid>
                    <Grid item xs={6} md={3}>
                      <Typography variant="subtitle2" color="text.secondary">
                        Quality
                      </Typography>
                      <Typography variant="body2">HD 1080p</Typography>
                    </Grid>
                  </Grid>
                </Box>
              )}
              {tabValue === 1 && (
                <Box>
                  <Box sx={{ mb: 3, p: 2, bgcolor: '#e3f2fd', borderRadius: 2 }}>
                    <Typography variant="subtitle1" sx={{ fontWeight: 'bold', mb: 1 }}>
                      Download Information
                    </Typography>
                    <Typography variant="body2">
                      Click on any of the download links below. If one server doesn't work, try another. 
                      All links are fast and secure. Enjoy your download!
                    </Typography>
                  </Box>
                  
                  {/* 1080p Download Links */}
                  <Paper sx={{ mb: 3, overflow: 'hidden', borderRadius: 2 }}>
                    <Box sx={{ bgcolor: '#1a237e', p: 2, color: 'white', display: 'flex', alignItems: 'center' }}>
                      <HighQualityIcon sx={{ mr: 1 }} />
                      <Typography variant="subtitle1" sx={{ fontWeight: 'bold' }}>
                        1080p Full HD Quality
                      </Typography>
                      <Chip 
                        label="350MB" 
                        size="small" 
                        sx={{ ml: 'auto', bgcolor: 'rgba(255,255,255,0.2)', color: 'white' }}
                      />
                    </Box>
                    <List>
                      {episode.downloadLinks['1080p'].map((link, index) => (
                        <ListItem 
                          key={index} 
                          divider={index !== episode.downloadLinks['1080p'].length - 1}
                          sx={{ 
                            '&:hover': { bgcolor: '#f5f5f5' },
                            transition: '0.2s'
                          }}
                        >
                          <ListItemText
                            primary={
                              <Box sx={{ display: 'flex', alignItems: 'center' }}>
                                <StorageIcon sx={{ mr: 1, color: 'primary.main' }} />
                                <Typography variant="body1" sx={{ fontWeight: 'medium' }}>
                                  {link.server}
                                </Typography>
                              </Box>
                            }
                            secondary="Fast Download, No Ads"
                          />
                          <Button
                            variant="contained"
                            color="primary"
                            startIcon={<CloudDownloadIcon />}
                            component={Link}
                            href={link.link}
                            target="_blank"
                            sx={{ borderRadius: 4 }}
                          >
                            Download
                          </Button>
                        </ListItem>
                      ))}
                    </List>
                  </Paper>
                  
                  {/* 720p Download Links */}
                  <Paper sx={{ mb: 3, overflow: 'hidden', borderRadius: 2 }}>
                    <Box sx={{ bgcolor: '#0d47a1', p: 2, color: 'white', display: 'flex', alignItems: 'center' }}>
                      <HighQualityIcon sx={{ mr: 1 }} />
                      <Typography variant="subtitle1" sx={{ fontWeight: 'bold' }}>
                        720p HD Quality
                      </Typography>
                      <Chip 
                        label="200MB" 
                        size="small" 
                        sx={{ ml: 'auto', bgcolor: 'rgba(255,255,255,0.2)', color: 'white' }}
                      />
                    </Box>
                    <List>
                      {episode.downloadLinks['720p'].map((link, index) => (
                        <ListItem 
                          key={index} 
                          divider={index !== episode.downloadLinks['720p'].length - 1}
                          sx={{ 
                            '&:hover': { bgcolor: '#f5f5f5' },
                            transition: '0.2s'
                          }}
                        >
                          <ListItemText
                            primary={
                              <Box sx={{ display: 'flex', alignItems: 'center' }}>
                                <StorageIcon sx={{ mr: 1, color: 'primary.main' }} />
                                <Typography variant="body1" sx={{ fontWeight: 'medium' }}>
                                  {link.server}
                                </Typography>
                              </Box>
                            }
                            secondary="Fast Download, No Ads"
                          />
                          <Button
                            variant="contained"
                            color="primary"
                            startIcon={<CloudDownloadIcon />}
                            component={Link}
                            href={link.link}
                            target="_blank"
                            sx={{ borderRadius: 4 }}
                          >
                            Download
                          </Button>
                        </ListItem>
                      ))}
                    </List>
                  </Paper>
                  
                  {/* 480p Download Links */}
                  <Paper sx={{ overflow: 'hidden', borderRadius: 2 }}>
                    <Box sx={{ bgcolor: '#2962ff', p: 2, color: 'white', display: 'flex', alignItems: 'center' }}>
                      <SpeedIcon sx={{ mr: 1 }} />
                      <Typography variant="subtitle1" sx={{ fontWeight: 'bold' }}>
                        480p Standard Quality
                      </Typography>
                      <Chip 
                        label="120MB" 
                        size="small" 
                        sx={{ ml: 'auto', bgcolor: 'rgba(255,255,255,0.2)', color: 'white' }}
                      />
                    </Box>
                    <List>
                      {episode.downloadLinks['480p'].map((link, index) => (
                        <ListItem 
                          key={index} 
                          divider={index !== episode.downloadLinks['480p'].length - 1}
                          sx={{ 
                            '&:hover': { bgcolor: '#f5f5f5' },
                            transition: '0.2s'
                          }}
                        >
                          <ListItemText
                            primary={
                              <Box sx={{ display: 'flex', alignItems: 'center' }}>
                                <StorageIcon sx={{ mr: 1, color: 'primary.main' }} />
                                <Typography variant="body1" sx={{ fontWeight: 'medium' }}>
                                  {link.server}
                                </Typography>
                              </Box>
                            }
                            secondary="Fast Download, No Ads"
                          />
                          <Button
                            variant="contained"
                            color="primary"
                            startIcon={<CloudDownloadIcon />}
                            component={Link}
                            href={link.link}
                            target="_blank"
                            sx={{ borderRadius: 4 }}
                          >
                            Download
                          </Button>
                        </ListItem>
                      ))}
                    </List>
                  </Paper>
                </Box>
              )}
              {tabValue === 2 && (
                <Box>
                  <Typography variant="h6" gutterBottom>
                    Episode Screenshots
                  </Typography>
                  <Grid container spacing={2}>
                    {episode.screenshots.map((screenshot, index) => (
                      <Grid item xs={12} sm={6} md={4} key={index}>
                        <Card sx={{ 
                          borderRadius: 2, 
                          overflow: 'hidden',
                          boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
                          transition: '0.3s',
                          '&:hover': {
                            transform: 'scale(1.02)',
                            boxShadow: '0 8px 16px rgba(0,0,0,0.2)',
                          }
                        }}>
                          <CardMedia
                            component="img"
                            height="160"
                            image={screenshot}
                            alt={`Screenshot ${index + 1}`}
                          />
                          <CardContent sx={{ p: 1, textAlign: 'center' }}>
                            <Typography variant="caption">
                              Screenshot {index + 1}
                            </Typography>
                          </CardContent>
                        </Card>
                      </Grid>
                    ))}
                  </Grid>
                </Box>
              )}
            </Box>
          </Box>
        </Grid>

        <Grid item xs={12} md={4}>
          {/* Episode Info Card */}
          <Card sx={{ mb: 4, borderRadius: 2, overflow: 'hidden', boxShadow: '0 4px 12px rgba(0,0,0,0.1)' }}>
            <CardMedia
              component="img"
              height="200"
              image={episode.image}
              alt={episode.title}
            />
            <CardContent>
              <Typography gutterBottom variant="h6" component="div" sx={{ fontWeight: 'bold' }}>
                {episode.title}
              </Typography>
              <Box sx={{ display: 'flex', gap: 1, mb: 2, flexWrap: 'wrap' }}>
                <Chip label={`Season ${episode.season}`} size="small" color="primary" />
                <Chip label={episode.duration} size="small" />
                <Chip label="HD" size="small" color="secondary" />
              </Box>
              <Typography variant="body2" color="text.secondary">
                {episode.description.substring(0, 120)}...
              </Typography>
            </CardContent>
            <CardActions sx={{ p: 2, bgcolor: '#f5f5f5' }}>
              <IconButton aria-label="add to favorites">
                <FavoriteIcon />
              </IconButton>
              <IconButton aria-label="share">
                <ShareIcon />
              </IconButton>
              <Button 
                size="small" 
                color="primary" 
                variant="contained"
                startIcon={<DownloadIcon />}
                sx={{ ml: 'auto', borderRadius: 4 }}
                onClick={() => setTabValue(1)}
              >
                Download
              </Button>
            </CardActions>
          </Card>

          {/* Related Episodes */}
          <Paper sx={{ p: 2, borderRadius: 2, mb: 4 }}>
            <Typography variant="h6" gutterBottom sx={{ fontWeight: 'bold', pb: 1, borderBottom: '2px solid #3f51b5' }}>
              Related Episodes
            </Typography>
            <Stack spacing={2} sx={{ mt: 2 }}>
              {relatedEpisodes.map((relEpisode) => (
                <Card
                  key={relEpisode.id}
                  sx={{
                    display: 'flex',
                    transition: '0.3s',
                    borderRadius: 2,
                    overflow: 'hidden',
                    boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
                    '&:hover': {
                      transform: 'translateY(-3px)',
                      boxShadow: '0 4px 10px rgba(0,0,0,0.2)',
                    },
                  }}
                  component={RouterLink}
                  to={`/episodes/${relEpisode.id}`}
                >
                  <CardMedia
                    component="img"
                    sx={{ width: 100 }}
                    image={relEpisode.image}
                    alt={relEpisode.title}
                  />
                  <Box sx={{ display: 'flex', flexDirection: 'column', width: '100%' }}>
                    <CardContent sx={{ flex: '1 0 auto', py: 1 }}>
                      <Typography component="div" variant="subtitle2" noWrap sx={{ fontWeight: 'medium' }}>
                        {relEpisode.title}
                      </Typography>
                      <Typography variant="caption" color="text.secondary" component="div">
                        {relEpisode.duration} • {relEpisode.releaseDate}
                      </Typography>
                    </CardContent>
                  </Box>
                </Card>
              ))}
            </Stack>
          </Paper>
        </Grid>
      </Grid>
    </Container>
  );
}

export default EpisodeDetailPage;
