import React, { useState, useEffect } from 'react';
import { Link as RouterLink, useNavigate } from 'react-router-dom';
import bgImage from "../assets/images/1.jpg";
import axios from 'axios';
import {
  Box,
  Typography,
  Button,
  Grid,
  Card,
  CardMedia,
  CardContent,
  CardActions,
  Container,
  Paper,
  Chip,
  Stack,
  Divider,
  TextField,
  InputAdornment,
  CircularProgress,
  Fade,
  Grow,
} from '@mui/material';
import DownloadIcon from '@mui/icons-material/Download';
import StarIcon from '@mui/icons-material/Star';
import SearchIcon from '@mui/icons-material/Search';
import WhatshotIcon from '@mui/icons-material/Whatshot';
import MovieIcon from '@mui/icons-material/Movie';
import TvIcon from '@mui/icons-material/Tv';
import MusicNoteIcon from '@mui/icons-material/MusicNote';
import DoubleArrowIcon from '@mui/icons-material/DoubleArrow';
import WelcomeCard from '../components/WelcomeCard';
import animeData from '../data/animeData.json';
import HeroSlider from '../components/HeroSlider';
import TrendingSection from '../components/TrendingSection';
import LatestReleasesSection from '../components/LatestReleasesSection';

function HomePage() {
  const [trendingAnime, setTrendingAnime] = useState([]);
  const [popularAnime, setPopularAnime] = useState([]);
  const [latestReleases, setLatestReleases] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [showWelcome, setShowWelcome] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    // Scroll to top when component mounts
    window.scrollTo(0, 0);
    
    // Set timer to hide welcome message after 4 seconds
    const timer = setTimeout(() => {
      setShowWelcome(false);
    }, 4000);
    
    // Simulate fetching data
    const trending = animeData.animeList
      .filter(anime => anime.rating >= 8.5)
      .slice(0, 6);
    
    const latest = animeData.animeList
      .sort((a, b) => new Date(b.updatedAt || b.year) - new Date(a.updatedAt || a.year))
      .slice(0, 8);
    
    setTrendingAnime(trending);
    setLatestReleases(latest);
    
    return () => clearTimeout(timer);
  }, []);

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleSearchSubmit = (event) => {
    event.preventDefault();
    if (searchTerm.trim()) {
      navigate(`/anime?search=${encodeURIComponent(searchTerm.trim())}`);
      
      // Reset search term after navigation
      setSearchTerm('');
    }
  };

  // Function to render anime cards
  const renderAnimeCard = (anime) => (
    <Grid item xs={12} sm={6} md={4} lg={3} key={anime.id}>
      <Card 
        sx={{ 
          height: '100%', 
          display: 'flex', 
          flexDirection: 'column',
          transition: 'transform 0.2s',
          '&:hover': {
            transform: 'scale(1.03)',
          }
        }}
      >
        <CardMedia
          component={RouterLink}
          to={`/anime/${anime.id}`}
          sx={{ 
            height: 340,
            backgroundPosition: 'top',
            position: 'relative',
            '&::after': {
              content: '""',
              position: 'absolute',
              bottom: 0,
              left: 0,
              width: '100%',
              height: '40%',
              background: 'linear-gradient(to top, rgba(0,0,0,0.8) 0%, rgba(0,0,0,0) 100%)',
            }
          }}
          image={anime.cover}
          title={anime.title}
        />
        <Box 
          sx={{ 
            position: 'absolute', 
            top: 10, 
            right: 10, 
            display: 'flex', 
            alignItems: 'center',
            bgcolor: 'rgba(0,0,0,0.7)',
            px: 1,
            py: 0.5,
            borderRadius: 1
          }}
        >
          <StarIcon sx={{ color: '#FFD700', fontSize: 18, mr: 0.5 }} />
          <Typography variant="body2" sx={{ color: 'white', fontWeight: 'bold' }}>
            {anime.rating}
          </Typography>
        </Box>
        
        <Box sx={{ position: 'absolute', bottom: 130, left: 10 }}>
          <Chip 
            label={anime.type} 
            size="small" 
            sx={{ 
              bgcolor: anime.type === 'Movie' ? '#e91e63' : '#3f51b5',
              color: 'white',
              fontWeight: 'bold',
            }} 
          />
        </Box>
        
        <CardContent sx={{ flexGrow: 1, pt: 2 }}>
          <Typography 
            gutterBottom 
            variant="h6" 
            component={RouterLink}
            to={`/anime/${anime.id}`}
            sx={{ 
              textDecoration: 'none',
              color: 'white',
              '&:hover': {
                color: '#ffb6c1',
              }
            }}
          >
            {anime.title}
          </Typography>
          <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
            <Typography variant="body2" color="text.secondary" sx={{ mr: 2 }}>
              {anime.episodes > 1 ? `${anime.episodes} Episodes` : 'Movie'}
            </Typography>
            <Divider orientation="vertical" flexItem sx={{ mr: 2 }} />
            <Typography variant="body2" color="text.secondary">
              {anime.status}
            </Typography>
          </Box>
          <Box sx={{ mb: 2 }}>
            {anime.genres.slice(0, 3).map((genre, index) => (
              <Chip 
                key={index} 
                label={genre} 
                size="small" 
                sx={{ mr: 0.5, mb: 0.5, bgcolor: 'rgba(255,255,255,0.1)' }} 
              />
            ))}
          </Box>
          <Typography variant="body2" color="text.secondary" sx={{ height: 60, overflow: 'hidden', textOverflow: 'ellipsis' }}>
            {anime.description}
          </Typography>
        </CardContent>
        <CardActions sx={{ p: 2, pt: 0 }}>
          <Button 
            variant="contained" 
            fullWidth 
            startIcon={<DownloadIcon />}
            component={RouterLink}
            to={`/anime/${anime.id}`}
            sx={{ 
              bgcolor: '#ffb6c1',
              color: '#000',
              '&:hover': {
                bgcolor: '#ff99a8',
              }
            }}
          >
            Download
          </Button>
        </CardActions>
      </Card>
    </Grid>
  );

  return (
    <div>
      {/* Welcome Card */}
      <Fade in={showWelcome} timeout={1000}>
        <Paper 
          elevation={6}
          sx={{
            position: 'fixed',
            top: '50%',
            left: '50%',
            transform: 'translate(-50%, -50%)',
            zIndex: 1000,
            maxWidth: '90%',
            width: '500px',
            padding: 4,
            textAlign: 'center',
            background: 'linear-gradient(135deg, #121212 0%, #2a2a2a 100%)',
            borderRadius: 2,
            border: '2px solid #ffb6c1',
            boxShadow: '0 10px 30px rgba(0,0,0,0.5)',
            display: showWelcome ? 'block' : 'none'
          }}
        >
          <Box 
            component="img"
            src="/logo.png"
            alt="AnimeHindiHub Logo"
            sx={{ 
              height: 80, 
              mb: 2,
              filter: 'drop-shadow(0 0 10px rgba(255,182,193,0.5))'
            }}
          />
          <Typography 
            variant="h4" 
            component="h1" 
            sx={{ 
              fontWeight: 'bold',
              mb: 1,
              color: 'white',
              textShadow: '0 2px 10px rgba(0,0,0,0.5)'
            }}
          >
            Welcome to <Box component="span" sx={{ color: '#ffb6c1' }}>AnimeHindiHub</Box>
          </Typography>
          <Typography 
            variant="body1" 
            sx={{ 
              mb: 3,
              color: 'rgba(255,255,255,0.7)'
            }}
          >
            Your #1 destination for HD anime in Hindi & English
          </Typography>
          <Box sx={{ display: 'flex', justifyContent: 'center', gap: 2 }}>
            <Button 
              variant="contained" 
              component={RouterLink} 
              to="/trending"
              sx={{ 
                bgcolor: '#ffb6c1',
                color: '#000',
                fontWeight: 'bold',
                '&:hover': {
                  bgcolor: '#ff99a8',
                }
              }}
            >
              Explore
            </Button>
          </Box>
        </Paper>
      </Fade>
      
      <Fade in={true} timeout={800}>
        <Box>
          {/* Hero Slider */}
          <HeroSlider />
          
          {/* Content Sections */}
          <Container maxWidth="xl" sx={{ mt: 6, mb: 8 }}>
            <Grow in={true} timeout={1200} style={{ transformOrigin: '0 0 0' }}>
              <div>
                <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
                  <Typography 
                    variant="h5" 
                    component="h2" 
                    sx={{ 
                      fontWeight: 'bold',
                      color: '#ffb6c1'
                    }}
                  >
                    Trending Now
                  </Typography>
                  
                  <Button 
                    variant="text" 
                    component={RouterLink} 
                    to="/trending"
                    endIcon={<DoubleArrowIcon />}
                    sx={{ 
                      color: 'white',
                      '&:hover': {
                        color: '#ffb6c1',
                      }
                    }}
                  >
                    View All
                  </Button>
                </Box>
                
                <TrendingSection animeList={trendingAnime} />
              </div>
            </Grow>
            
            <Grow in={true} timeout={1500} style={{ transformOrigin: '0 0 0' }}>
              <div>
                <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mt: 6, mb: 3 }}>
                  <Typography 
                    variant="h5" 
                    component="h2" 
                    sx={{ 
                      fontWeight: 'bold',
                      color: '#ffb6c1'
                    }}
                  >
                    Latest Releases
                  </Typography>
                  
                  <Button 
                    variant="text" 
                    component={RouterLink} 
                    to="/anime"
                    endIcon={<DoubleArrowIcon />}
                    sx={{ 
                      color: 'white',
                      '&:hover': {
                        color: '#ffb6c1',
                      }
                    }}
                  >
                    View All
                  </Button>
                </Box>
                
                <LatestReleasesSection animeList={latestReleases} />
              </div>
            </Grow>
          </Container>
        </Box>
      </Fade>
    </div>
  );
}

export default HomePage;
