import React, { useState, useEffect } from 'react';
import { Link as RouterLink, useNavigate } from 'react-router-dom';
import {
  AppBar,
  Box,
  Toolbar,
  Typography,
  Button,
  IconButton,
  Drawer,
  List,
  ListItem,
  ListItemIcon,
  ListItemText,
  Divider,
  Avatar,
  Menu,
  MenuItem,
  Badge,
  useMediaQuery,
  useTheme,
  Stack,
  Hidden,
} from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';
import CloseIcon from '@mui/icons-material/Close';
import HomeIcon from '@mui/icons-material/Home';
import WhatshotIcon from '@mui/icons-material/Whatshot';
import MovieIcon from '@mui/icons-material/Movie';
import TvIcon from '@mui/icons-material/Tv';
import SearchIcon from '@mui/icons-material/Search';
import BookmarkIcon from '@mui/icons-material/Bookmark';
import HistoryIcon from '@mui/icons-material/History';
import SettingsIcon from '@mui/icons-material/Settings';
import LogoutIcon from '@mui/icons-material/Logout';
import PersonIcon from '@mui/icons-material/Person';
import NotificationsIcon from '@mui/icons-material/Notifications';
import InfoIcon from '@mui/icons-material/Info';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import DashboardIcon from '@mui/icons-material/Dashboard';
import AuthDialog from './AuthDialog';

const Header = ({ user, onLogout, openAuthDialog }) => {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));
  const navigate = useNavigate();
  
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [profileMenuAnchor, setProfileMenuAnchor] = useState(null);
  
  const handleDrawerToggle = () => {
    setDrawerOpen(!drawerOpen);
  };
  
  const handleProfileMenuOpen = (event) => {
    setProfileMenuAnchor(event.currentTarget);
  };
  
  const handleProfileMenuClose = () => {
    setProfileMenuAnchor(null);
  };
  
  const handleProfileNav = (path) => {
    handleProfileMenuClose();
    navigate(path);
  };
  
  const handleLogout = () => {
    handleProfileMenuClose();
    onLogout();
  };
  
  const menuItems = [
    { text: 'Home', icon: <HomeIcon />, link: '/' },
    { text: 'Anime', icon: <MovieIcon />, link: '/anime' },
    { text: 'Movies', icon: <MovieIcon />, link: '/movies' },
    { text: 'TV Series', icon: <TvIcon />, link: '/series' },
    { text: 'Trending', icon: <WhatshotIcon />, link: '/trending' },
    { text: 'Search', icon: <SearchIcon />, link: '/search' },
    { text: 'About', icon: <InfoIcon />, link: '/about' },
  ];
  
  // Drawer content
  const drawerContent = (
    <Box sx={{ width: 280, bgcolor: '#1a1a1a', height: '100%' }}>
      <Box sx={{ py: 2, px: 3, display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
        <Typography variant="h6" component={RouterLink} to="/" sx={{ 
          color: 'white', 
          textDecoration: 'none',
          fontWeight: 'bold',
          display: 'flex',
          alignItems: 'center'
        }}>
          <MovieIcon sx={{ mr: 1, color: '#ffb6c1' }} />
          AnimeStream
        </Typography>
      </Box>
      
      <Divider sx={{ bgcolor: 'rgba(255,255,255,0.1)' }} />
      
      {user ? (
        <Box sx={{ py: 2, px: 2, display: 'flex', alignItems: 'center' }}>
          <Avatar sx={{ bgcolor: '#ffb6c1', color: '#000' }}>
            {user.username.charAt(0).toUpperCase()}
          </Avatar>
          <Box sx={{ ml: 2 }}>
            <Typography variant="subtitle1" sx={{ fontWeight: 'bold' }}>
              {user.username}
            </Typography>
            <Typography variant="body2" color="text.secondary">
              {user.email}
            </Typography>
          </Box>
        </Box>
      ) : (
        <Box sx={{ py: 2, px: 2, display: 'flex', justifyContent: 'center' }}>
          <Button 
            variant="contained" 
            onClick={openAuthDialog}
            sx={{ 
              bgcolor: '#ffb6c1',
              color: '#000',
              fontWeight: 'bold',
              '&:hover': {
                bgcolor: '#ff99a8',
              }
            }}
          >
            Sign In
          </Button>
        </Box>
      )}
      
      <Divider sx={{ bgcolor: 'rgba(255,255,255,0.1)' }} />
      
      <List>
        {menuItems.map((item) => (
          <ListItem
            key={item.text}
            button
            component={RouterLink}
            to={item.link}
            onClick={() => { handleDrawerToggle(); handleProfileNav(item.link); }}
          >
            <ListItemIcon sx={{ color: '#ffb6c1', minWidth: 40 }}>
              {item.icon}
            </ListItemIcon>
            <ListItemText primary={item.text} />
          </ListItem>
        ))}
        
        {user ? (
          <>
            <Divider sx={{ my: 1, borderColor: 'rgba(255,255,255,0.12)' }} />
            <ListItem 
              button
              component={RouterLink}
              to="/profile?tab=dashboard"
              onClick={() => { handleDrawerToggle(); handleProfileNav('/profile?tab=dashboard'); }}
            >
              <ListItemIcon sx={{ color: '#ffb6c1', minWidth: 40 }}>
                <DashboardIcon />
              </ListItemIcon>
              <ListItemText primary="Dashboard" />
            </ListItem>
            <ListItem 
              button
              component={RouterLink}
              to="/profile?tab=history"
              onClick={() => { handleDrawerToggle(); handleProfileNav('/profile?tab=history'); }}
            >
              <ListItemIcon sx={{ color: '#ffb6c1', minWidth: 40 }}>
                <HistoryIcon />
              </ListItemIcon>
              <ListItemText primary="History" />
            </ListItem>
            <ListItem 
              button
              component={RouterLink}
              to="/profile?tab=favorites"
              onClick={() => { handleDrawerToggle(); handleProfileNav('/profile?tab=favorites'); }}
            >
              <ListItemIcon sx={{ color: '#ffb6c1', minWidth: 40 }}>
                <BookmarkIcon />
              </ListItemIcon>
              <ListItemText primary="Favorites" />
            </ListItem>
            <ListItem button onClick={() => { handleDrawerToggle(); handleLogout(); }}>
              <ListItemIcon sx={{ color: '#ffb6c1', minWidth: 40 }}>
                <LogoutIcon />
              </ListItemIcon>
              <ListItemText primary="Logout" />
            </ListItem>
          </>
        ) : (
          <ListItem button onClick={() => { handleDrawerToggle(); openAuthDialog(); }}>
            <ListItemIcon sx={{ color: '#ffb6c1', minWidth: 40 }}>
              <AccountCircleIcon />
            </ListItemIcon>
            <ListItemText primary="Sign In" />
          </ListItem>
        )}
      </List>
    </Box>
  );

  return (
    <>
      <AppBar position="sticky" sx={{ bgcolor: '#121212', boxShadow: 3 }}>
        <Toolbar>
          <IconButton
            edge="start"
            color="inherit"
            aria-label="menu"
            onClick={handleDrawerToggle}
            sx={{ mr: 2, display: { sm: 'block', md: 'none' } }}
          >
            <MenuIcon />
          </IconButton>
          
          <Typography 
            variant="h6" 
            component={RouterLink} 
            to="/" 
            sx={{ 
              flexGrow: 1, 
              display: 'flex', 
              alignItems: 'center',
              color: 'white',
              textDecoration: 'none',
              fontWeight: 'bold',
            }}
          >
            <MovieIcon sx={{ mr: 1, color: '#ffb6c1' }} />
            <Box component="span" sx={{ display: { xs: 'none', sm: 'inline' } }}>
              AnimeStream
            </Box>
          </Typography>
          
          {!isMobile && (
            <Stack direction="row" spacing={1} sx={{ mr: 2 }}>
              {menuItems.slice(0, 6).map((item) => (
                <Button 
                  key={item.text}
                  color="inherit" 
                  component={RouterLink} 
                  to={item.link}
                  sx={{ 
                    fontSize: '0.9rem',
                    '&:hover': { color: '#ffb6c1' }
                  }}
                >
                  {item.text}
                </Button>
              ))}
            </Stack>
          )}
          
          {user ? (
            <Box sx={{ display: 'flex', alignItems: 'center' }}>
              {!isMobile && (
                <IconButton color="inherit" sx={{ mr: 1 }}>
                  <Badge badgeContent={3} color="error">
                    <NotificationsIcon />
                  </Badge>
                </IconButton>
              )}
              
              <IconButton
                onClick={handleProfileMenuOpen}
                size="small"
                edge="end"
                aria-label="account of current user"
                aria-haspopup="true"
              >
                <Avatar sx={{ bgcolor: '#ffb6c1', color: '#000', width: 32, height: 32 }}>
                  {user.username.charAt(0).toUpperCase()}
                </Avatar>
              </IconButton>
            </Box>
          ) : (
            <Button 
              variant="contained" 
              onClick={openAuthDialog}
              sx={{ 
                bgcolor: '#ffb6c1',
                color: '#000',
                fontWeight: 'bold',
                '&:hover': {
                  bgcolor: '#ff99a8',
                }
              }}
            >
              Sign In
            </Button>
          )}
        </Toolbar>
      </AppBar>
      
      <Drawer
        anchor="left"
        open={drawerOpen}
        onClose={handleDrawerToggle}
        ModalProps={{
          keepMounted: true, // Better mobile performance
        }}
        PaperProps={{
          sx: {
            bgcolor: '#1a1a1a',
            color: 'white',
          }
        }}
      >
        <Box sx={{ p: 2, display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          <Typography variant="h6" sx={{ fontWeight: 'bold' }}>
            <Box component="span" sx={{ color: '#ffb6c1' }}>Anime</Box>Hindi4U
          </Typography>
          <IconButton color="inherit" onClick={handleDrawerToggle}>
            <CloseIcon />
          </IconButton>
        </Box>
        
        {user && (
          <>
            <Box sx={{ p: 2, display: 'flex', alignItems: 'center' }}>
              <Avatar
                sx={{
                  width: 42,
                  height: 42,
                  bgcolor: '#ffb6c1',
                  color: 'black',
                  mr: 2,
                }}
              >
                {user.username ? user.username.charAt(0).toUpperCase() : 'U'}
              </Avatar>
              <Box>
                <Typography variant="subtitle1" sx={{ fontWeight: 'bold' }}>
                  {user.username}
                </Typography>
                <Typography variant="body2" sx={{ color: 'rgba(255,255,255,0.7)' }}>
                  {user.email}
                </Typography>
              </Box>
            </Box>
            <Divider sx={{ borderColor: 'rgba(255,255,255,0.12)' }} />
          </>
        )}
        
        <List>
          {menuItems.map((item) => (
            <ListItem
              key={item.text}
              button
              component={RouterLink}
              to={item.link}
              onClick={() => { handleDrawerToggle(); handleProfileNav(item.link); }}
            >
              <ListItemIcon sx={{ color: '#ffb6c1', minWidth: 40 }}>
                {item.icon}
              </ListItemIcon>
              <ListItemText primary={item.text} />
            </ListItem>
          ))}
          
          {user ? (
            <>
              <Divider sx={{ my: 1, borderColor: 'rgba(255,255,255,0.12)' }} />
              <ListItem 
                button
                component={RouterLink}
                to="/profile?tab=dashboard"
                onClick={() => { handleDrawerToggle(); handleProfileNav('/profile?tab=dashboard'); }}
              >
                <ListItemIcon sx={{ color: '#ffb6c1', minWidth: 40 }}>
                  <DashboardIcon />
                </ListItemIcon>
                <ListItemText primary="Dashboard" />
              </ListItem>
              <ListItem 
                button
                component={RouterLink}
                to="/profile?tab=history"
                onClick={() => { handleDrawerToggle(); handleProfileNav('/profile?tab=history'); }}
              >
                <ListItemIcon sx={{ color: '#ffb6c1', minWidth: 40 }}>
                  <HistoryIcon />
                </ListItemIcon>
                <ListItemText primary="History" />
              </ListItem>
              <ListItem 
                button
                component={RouterLink}
                to="/profile?tab=favorites"
                onClick={() => { handleDrawerToggle(); handleProfileNav('/profile?tab=favorites'); }}
              >
                <ListItemIcon sx={{ color: '#ffb6c1', minWidth: 40 }}>
                  <BookmarkIcon />
                </ListItemIcon>
                <ListItemText primary="Favorites" />
              </ListItem>
              <ListItem button onClick={() => { handleDrawerToggle(); handleLogout(); }}>
                <ListItemIcon sx={{ color: '#ffb6c1', minWidth: 40 }}>
                  <LogoutIcon />
                </ListItemIcon>
                <ListItemText primary="Logout" />
              </ListItem>
            </>
          ) : (
            <ListItem button onClick={() => { handleDrawerToggle(); openAuthDialog(); }}>
              <ListItemIcon sx={{ color: '#ffb6c1', minWidth: 40 }}>
                <AccountCircleIcon />
              </ListItemIcon>
              <ListItemText primary="Sign In" />
            </ListItem>
          )}
        </List>
      </Drawer>
      
      <Menu
        anchorEl={profileMenuAnchor}
        open={Boolean(profileMenuAnchor)}
        onClose={handleProfileMenuClose}
        PaperProps={{
          sx: {
            bgcolor: '#1a1a1a',
            color: 'white',
            mt: 1,
            minWidth: 200,
          },
        }}
        transformOrigin={{ horizontal: 'right', vertical: 'top' }}
        anchorOrigin={{ horizontal: 'right', vertical: 'bottom' }}
      >
        <Box sx={{ px: 2, py: 1 }}>
          <Typography variant="subtitle1" sx={{ fontWeight: 'bold' }}>
            {user?.username}
          </Typography>
          <Typography variant="body2" sx={{ color: 'rgba(255,255,255,0.7)' }}>
            {user?.email}
          </Typography>
        </Box>
        <Divider sx={{ borderColor: 'rgba(255,255,255,0.12)' }} />
        <MenuItem onClick={() => handleProfileNav('/profile?tab=dashboard')}>
          <ListItemIcon sx={{ color: '#ffb6c1' }}>
            <DashboardIcon fontSize="small" />
          </ListItemIcon>
          <ListItemText>Dashboard</ListItemText>
        </MenuItem>
        <MenuItem onClick={() => handleProfileNav('/profile?tab=history')}>
          <ListItemIcon sx={{ color: '#ffb6c1' }}>
            <HistoryIcon fontSize="small" />
          </ListItemIcon>
          <ListItemText>History</ListItemText>
        </MenuItem>
        <MenuItem onClick={() => handleProfileNav('/profile?tab=favorites')}>
          <ListItemIcon sx={{ color: '#ffb6c1' }}>
            <BookmarkIcon fontSize="small" />
          </ListItemIcon>
          <ListItemText>Favorites</ListItemText>
        </MenuItem>
        <Divider sx={{ borderColor: 'rgba(255,255,255,0.12)' }} />
        <MenuItem onClick={handleLogout}>
          <ListItemIcon sx={{ color: '#ffb6c1' }}>
            <LogoutIcon fontSize="small" />
          </ListItemIcon>
          <ListItemText>Logout</ListItemText>
        </MenuItem>
      </Menu>
    </>
  );
};

export default Header;
