import React, { useState, useEffect } from 'react';
import { Link as RouterLink } from 'react-router-dom';
import animeData from '../data/animeData.json';
import {
  Box,
  Container,
  Typography,
  Grid,
  Card,
  CardMedia,
  CardContent,
  CardActions,
  Button,
  Chip,
  CircularProgress,
  Divider,
  Tabs,
  Tab,
} from '@mui/material';
import PlayArrowIcon from '@mui/icons-material/PlayArrow';
import DownloadIcon from '@mui/icons-material/Download';
import StarIcon from '@mui/icons-material/Star';
import WhatshotIcon from '@mui/icons-material/Whatshot';

function TrendingPage() {
  const [trendingAnime, setTrendingAnime] = useState([]);
  const [loading, setLoading] = useState(true);
  const [tabValue, setTabValue] = useState(0);

  useEffect(() => {
    // Load trending anime data from the comprehensive JSON file
    const loadTrendingData = () => {
      try {
        setLoading(true);
        
        // Get all anime from the JSON data
        const allAnime = animeData.animeList || [];
        
        // Sort by views for most popular content
        const sortedByViews = [...allAnime].sort((a, b) => b.views - a.views);
        // Sort by rating for top rated content
        const sortedByRating = [...allAnime].sort((a, b) => b.rating - a.rating);
        // Sort by year for newest content
        const sortedByYear = [...allAnime].sort((a, b) => b.year - a.year);
        
        // Combine all sorted lists with most popular first
        setTrendingAnime({
          popular: sortedByViews.slice(0, 12),
          topRated: sortedByRating.slice(0, 12),
          newest: sortedByYear.slice(0, 12)
        });
        
        setLoading(false);
      } catch (error) {
        console.error("Failed to load trending anime data:", error);
        setLoading(false);
      }
    };

    loadTrendingData();
  }, []);

  const handleTabChange = (event, newValue) => {
    setTabValue(newValue);
  };

  const renderAnimeCard = (anime) => (
    <Grid item xs={12} sm={6} md={4} lg={3} key={anime.id}>
      <Card 
        sx={{ 
          height: '100%', 
          display: 'flex', 
          flexDirection: 'column',
          bgcolor: 'rgba(255, 255, 255, 0.05)',
          transition: 'transform 0.3s ease-in-out',
          '&:hover': {
            transform: 'scale(1.03)',
            boxShadow: '0 8px 16px rgba(0,0,0,0.3)',
            bgcolor: 'rgba(255, 255, 255, 0.08)',
          }
        }}
      >
        <Box sx={{ position: 'relative' }}>
          <CardMedia
            component="img"
            height={250}
            image={anime.cover}
            alt={anime.title}
            sx={{ objectFit: 'cover' }}
          />
          <Box
            sx={{
              position: 'absolute',
              top: 10,
              right: 10,
              display: 'flex',
              alignItems: 'center',
              bgcolor: 'rgba(0, 0, 0, 0.7)',
              borderRadius: 1,
              px: 1,
              py: 0.5,
            }}
          >
            <StarIcon sx={{ color: '#FFD700', fontSize: 18, mr: 0.5 }} />
            <Typography variant="body2" sx={{ fontWeight: 'bold', color: 'white' }}>
              {anime.rating}
            </Typography>
          </Box>
          {anime.type === 'Movie' && (
            <Chip
              label="Movie"
              size="small"
              sx={{
                position: 'absolute',
                top: 10,
                left: 10,
                bgcolor: '#e53935',
                color: 'white',
                fontWeight: 'bold',
              }}
            />
          )}
          {anime.status === 'Ongoing' && (
            <Chip
              label="Ongoing"
              size="small"
              sx={{
                position: 'absolute',
                bottom: 10,
                left: 10,
                bgcolor: '#2196f3',
                color: 'white',
                fontWeight: 'bold',
              }}
            />
          )}
          
          {tabValue === 0 && (
            <Box
              sx={{
                position: 'absolute',
                top: 10,
                left: anime.type === 'Movie' ? 80 : 10,
                display: 'flex',
                alignItems: 'center',
                bgcolor: 'rgba(255, 99, 71, 0.8)',
                borderRadius: 1,
                px: 1,
                py: 0.5,
              }}
            >
              <WhatshotIcon sx={{ color: 'white', fontSize: 18, mr: 0.5 }} />
              <Typography variant="body2" sx={{ fontWeight: 'bold', color: 'white' }}>
                {anime.views > 1000000 
                  ? `${(anime.views / 1000000).toFixed(1)}M` 
                  : `${(anime.views / 1000).toFixed(0)}K`}
              </Typography>
            </Box>
          )}
        </Box>
        
        <CardContent sx={{ flexGrow: 1 }}>
          <Typography variant="h6" component="div" sx={{ fontWeight: 'bold', mb: 1 }}>
            {anime.title}
          </Typography>
          
          <Box sx={{ display: 'flex', mb: 1, flexWrap: 'wrap', gap: 0.5 }}>
            {anime.genres.slice(0, 3).map((genre, index) => (
              <Chip 
                key={index} 
                label={genre} 
                size="small" 
                sx={{ 
                  bgcolor: 'rgba(255, 255, 255, 0.1)',
                  color: 'text.secondary',
                  fontSize: '0.75rem',
                }} 
              />
            ))}
          </Box>
          
          <Typography variant="body2" color="text.secondary">
            {anime.episodes > 1 ? `${anime.episodes} Episodes • ` : 'Movie • '}
            {anime.year} • {anime.language}
          </Typography>
        </CardContent>
        
        <CardActions sx={{ p: 2, pt: 0 }}>
          <Button
            component={RouterLink}
            to={`/anime/${anime.id}`}
            variant="contained"
            size="small"
            startIcon={<PlayArrowIcon />}
            sx={{ 
              bgcolor: '#ffb6c1',
              color: '#000',
              fontWeight: 'bold',
              '&:hover': {
                bgcolor: '#ff99a8',
              },
              flexGrow: 1
            }}
          >
            Watch Now
          </Button>
          
          <Button
            component={RouterLink}
            to={`/anime/${anime.id}`}
            variant="outlined"
            size="small"
            startIcon={<DownloadIcon />}
            sx={{ 
              borderColor: 'rgba(255, 255, 255, 0.3)',
              color: 'white',
              '&:hover': {
                borderColor: 'white',
                bgcolor: 'rgba(255, 255, 255, 0.1)',
              },
              ml: 1
            }}
          >
            Download
          </Button>
        </CardActions>
      </Card>
    </Grid>
  );

  return (
    <Box sx={{ bgcolor: '#121212', minHeight: '100vh', pt: 3, pb: 6 }}>
      <Container maxWidth="xl">
        <Box sx={{ mb: 4 }}>
          <Typography 
            variant="h4" 
            component="h1" 
            sx={{ 
              fontWeight: 'bold',
              mb: 1,
              color: 'white',
              textAlign: { xs: 'center', md: 'left' }
            }}
          >
            Trending Anime
          </Typography>
          <Typography 
            variant="body1" 
            color="text.secondary"
            sx={{ 
              mb: 2,
              textAlign: { xs: 'center', md: 'left' }
            }}
          >
            Discover the most popular and trending anime in Hindi
          </Typography>
          <Divider sx={{ borderColor: 'rgba(255, 255, 255, 0.1)' }} />
        </Box>

        <Box sx={{ borderBottom: 1, borderColor: 'rgba(255, 255, 255, 0.1)', mb: 3 }}>
          <Tabs 
            value={tabValue} 
            onChange={handleTabChange}
            textColor="inherit"
            sx={{
              '& .MuiTab-root': {
                color: 'rgba(255,255,255,0.7)',
                '&.Mui-selected': {
                  color: '#ffb6c1',
                },
              },
              '& .MuiTabs-indicator': {
                backgroundColor: '#ffb6c1',
              },
            }}
          >
            <Tab label="Most Popular" />
            <Tab label="Top Rated" />
            <Tab label="Newest" />
          </Tabs>
        </Box>

        {loading ? (
          <Box sx={{ display: 'flex', justifyContent: 'center', py: 8 }}>
            <CircularProgress sx={{ color: '#ffb6c1' }} />
          </Box>
        ) : (
          <Box role="tabpanel" hidden={tabValue !== 0} id={`trending-tabpanel-0`}>
            {tabValue === 0 && trendingAnime.popular && (
              <Grid container spacing={3}>
                {trendingAnime.popular.map(anime => renderAnimeCard(anime))}
              </Grid>
            )}
          </Box>
        )}

        {!loading && (
          <Box role="tabpanel" hidden={tabValue !== 1} id={`trending-tabpanel-1`}>
            {tabValue === 1 && trendingAnime.topRated && (
              <Grid container spacing={3}>
                {trendingAnime.topRated.map(anime => renderAnimeCard(anime))}
              </Grid>
            )}
          </Box>
        )}

        {!loading && (
          <Box role="tabpanel" hidden={tabValue !== 2} id={`trending-tabpanel-2`}>
            {tabValue === 2 && trendingAnime.newest && (
              <Grid container spacing={3}>
                {trendingAnime.newest.map(anime => renderAnimeCard(anime))}
              </Grid>
            )}
          </Box>
        )}
      </Container>
    </Box>
  );
}

export default TrendingPage;
