import React, { useState, useEffect } from 'react';
import { Link as RouterLink, useNavigate } from 'react-router-dom';
import bgImage from "../assets/images/1.jpg";
import axios from 'axios';
import {
  Box,
  Typography,
  Button,
  Grid,
  Card,
  CardMedia,
  CardContent,
  CardActions,
  Container,
  Paper,
  Chip,
  Stack,
  Divider,
  TextField,
  InputAdornment,
  CircularProgress,
} from '@mui/material';
import DownloadIcon from '@mui/icons-material/Download';
import StarIcon from '@mui/icons-material/Star';
import SearchIcon from '@mui/icons-material/Search';
import WhatshotIcon from '@mui/icons-material/Whatshot';
import MovieIcon from '@mui/icons-material/Movie';
import TvIcon from '@mui/icons-material/Tv';
import MusicNoteIcon from '@mui/icons-material/MusicNote';
import WelcomeCard from '../components/WelcomeCard';
import animeData from '../data/animeData.json';

function HomePage() {
  const [trendingAnime, setTrendingAnime] = useState([]);
  const [popularAnime, setPopularAnime] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    // Load anime data from the comprehensive JSON file
    const loadAnimeData = () => {
      try {
        setLoading(true);
        
        // Get the anime list from the JSON data
        const allAnime = animeData.animeList || [];
        
        // Sort by rating for popular anime
        const sortedByRating = [...allAnime].sort((a, b) => b.rating - a.rating);
        setPopularAnime(sortedByRating.slice(0, 6));
        
        // Get most recent anime for trending
        const sortedByYear = [...allAnime].sort((a, b) => b.year - a.year);
        setTrendingAnime(sortedByYear.slice(0, 4));
        
        setLoading(false);
      } catch (error) {
        console.error("Failed to load anime data:", error);
        setLoading(false);
      }
    };

    loadAnimeData();
  }, []);

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleSearchSubmit = (event) => {
    event.preventDefault();
    if (searchTerm.trim()) {
      navigate(`/anime?search=${encodeURIComponent(searchTerm.trim())}`);
      
      // Reset search term after navigation
      setSearchTerm('');
    }
  };

  // Function to render anime cards
  const renderAnimeCard = (anime) => (
    <Grid item xs={12} sm={6} md={4} lg={3} key={anime.id}>
      <Card 
        sx={{ 
          height: '100%', 
          display: 'flex', 
          flexDirection: 'column',
          transition: 'transform 0.2s',
          '&:hover': {
            transform: 'scale(1.03)',
          }
        }}
      >
        <CardMedia
          component={RouterLink}
          to={`/anime/${anime.id}`}
          sx={{ 
            height: 340,
            backgroundPosition: 'top',
            position: 'relative',
            '&::after': {
              content: '""',
              position: 'absolute',
              bottom: 0,
              left: 0,
              width: '100%',
              height: '40%',
              background: 'linear-gradient(to top, rgba(0,0,0,0.8) 0%, rgba(0,0,0,0) 100%)',
            }
          }}
          image={anime.cover}
          title={anime.title}
        />
        <Box 
          sx={{ 
            position: 'absolute', 
            top: 10, 
            right: 10, 
            display: 'flex', 
            alignItems: 'center',
            bgcolor: 'rgba(0,0,0,0.7)',
            px: 1,
            py: 0.5,
            borderRadius: 1
          }}
        >
          <StarIcon sx={{ color: '#FFD700', fontSize: 18, mr: 0.5 }} />
          <Typography variant="body2" sx={{ color: 'white', fontWeight: 'bold' }}>
            {anime.rating}
          </Typography>
        </Box>
        
        <Box sx={{ position: 'absolute', bottom: 130, left: 10 }}>
          <Chip 
            label={anime.type} 
            size="small" 
            sx={{ 
              bgcolor: anime.type === 'Movie' ? '#e91e63' : '#3f51b5',
              color: 'white',
              fontWeight: 'bold',
            }} 
          />
        </Box>
        
        <CardContent sx={{ flexGrow: 1, pt: 2 }}>
          <Typography 
            gutterBottom 
            variant="h6" 
            component={RouterLink}
            to={`/anime/${anime.id}`}
            sx={{ 
              textDecoration: 'none',
              color: 'white',
              '&:hover': {
                color: '#ffb6c1',
              }
            }}
          >
            {anime.title}
          </Typography>
          <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
            <Typography variant="body2" color="text.secondary" sx={{ mr: 2 }}>
              {anime.episodes > 1 ? `${anime.episodes} Episodes` : 'Movie'}
            </Typography>
            <Divider orientation="vertical" flexItem sx={{ mr: 2 }} />
            <Typography variant="body2" color="text.secondary">
              {anime.status}
            </Typography>
          </Box>
          <Box sx={{ mb: 2 }}>
            {anime.genres.slice(0, 3).map((genre, index) => (
              <Chip 
                key={index} 
                label={genre} 
                size="small" 
                sx={{ mr: 0.5, mb: 0.5, bgcolor: 'rgba(255,255,255,0.1)' }} 
              />
            ))}
          </Box>
          <Typography variant="body2" color="text.secondary" sx={{ height: 60, overflow: 'hidden', textOverflow: 'ellipsis' }}>
            {anime.description}
          </Typography>
        </CardContent>
        <CardActions sx={{ p: 2, pt: 0 }}>
          <Button 
            variant="contained" 
            fullWidth 
            startIcon={<DownloadIcon />}
            component={RouterLink}
            to={`/anime/${anime.id}`}
            sx={{ 
              bgcolor: '#ffb6c1',
              color: '#000',
              '&:hover': {
                bgcolor: '#ff99a8',
              }
            }}
          >
            Download
          </Button>
        </CardActions>
      </Card>
    </Grid>
  );

  return (
    <>
      <WelcomeCard />
      <Box sx={{ bgcolor: '#121212' }}>
        {/* Hero Section */}
        <Paper
          sx={{
            position: 'relative',
            backgroundColor: 'grey.900',
            color: '#fff',
            mb: 0,
            backgroundSize: 'cover',
            backgroundRepeat: 'no-repeat',
            backgroundPosition: 'center',
            backgroundImage: `url(${bgImage})`,
            height: '500px',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            borderRadius: 0,
          }}
        >
          {/* Background overlay */}
          <Box
            sx={{
              position: 'absolute',
              top: 0,
              bottom: 0,
              right: 0,
              left: 0,
              backgroundColor: 'rgba(0,0,0,.7)',
              backgroundImage: 'linear-gradient(to top, rgba(0,0,0,0.9) 0%, rgba(0,0,0,0.6) 50%, rgba(0,0,0,0.4) 100%)',
            }}
          />
          <Container maxWidth="md" sx={{ position: 'relative', textAlign: 'center' }}>
            <Typography 
              component="h1" 
              variant="h2" 
              color="inherit" 
              gutterBottom 
              sx={{ 
                fontWeight: 'bold',
                textShadow: '0 2px 10px rgba(0,0,0,0.7)',
                mb: 3,
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center'
              }}
            >
              <MusicNoteIcon sx={{ mr: 2, fontSize: 40, color: '#ffb6c1' }} />
              AnimeStream
            </Typography>
            <Typography 
              variant="h5" 
              color="inherit" 
              paragraph
              sx={{ 
                maxWidth: '700px',
                mx: 'auto',
                mb: 4,
                opacity: 0.9
              }}
            >
              Download all episodes in high quality
            </Typography>
            
            <form onSubmit={handleSearchSubmit}>
              <TextField
                fullWidth
                placeholder="Search anime..."
                variant="outlined"
                value={searchTerm}
                onChange={handleSearchChange}
                InputProps={{
                  startAdornment: (
                    <InputAdornment position="start">
                      <SearchIcon sx={{ color: 'white' }} />
                    </InputAdornment>
                  ),
                  sx: { 
                    bgcolor: 'rgba(255,255,255,0.1)', 
                    borderRadius: 2,
                    color: 'white',
                    '& .MuiOutlinedInput-notchedOutline': {
                      borderColor: 'rgba(255,255,255,0.3)',
                    },
                    '&:hover .MuiOutlinedInput-notchedOutline': {
                      borderColor: 'rgba(255,255,255,0.5)',
                    },
                    '&.Mui-focused .MuiOutlinedInput-notchedOutline': {
                      borderColor: '#ffb6c1',
                    },
                    '&::placeholder': {
                      color: 'rgba(255,255,255,0.7)',
                    },
                  }
                }}
                sx={{ 
                  maxWidth: '600px', 
                  mx: 'auto',
                  mb: 3,
                  '& .MuiInputBase-input': {
                    color: 'white',
                  }
                }}
              />
              <Button 
                type="submit" 
                variant="contained"
                sx={{ 
                  display: 'none' // Hidden submit button for form submission
                }}
              >
                Search
              </Button>
            </form>
            
            <Stack direction="row" spacing={2} justifyContent="center">
              <Button
                variant="contained"
                component={RouterLink}
                to="/trending"
                size="large"
                startIcon={<WhatshotIcon />}
                sx={{ 
                  py: 1.2,
                  borderRadius: 2,
                  fontWeight: 'bold',
                  bgcolor: '#ffb6c1',
                  color: '#000',
                  '&:hover': {
                    bgcolor: '#ff99a8',
                  }
                }}
              >
                Trending
              </Button>
              
              <Button
                variant="contained"
                component={RouterLink}
                to="/movies"
                size="large"
                startIcon={<MovieIcon />}
                sx={{ 
                  py: 1.2,
                  borderRadius: 2,
                  fontWeight: 'bold',
                  bgcolor: '#e91e63',
                  color: 'white',
                  '&:hover': {
                    bgcolor: '#c2185b',
                  }
                }}
              >
                Movies
              </Button>
              
              <Button
                variant="contained"
                component={RouterLink}
                to="/series"
                size="large"
                startIcon={<TvIcon />}
                sx={{ 
                  py: 1.2,
                  borderRadius: 2,
                  fontWeight: 'bold',
                  bgcolor: '#3f51b5',
                  color: 'white',
                  '&:hover': {
                    bgcolor: '#303f9f',
                  }
                }}
              >
                TV Series
              </Button>
            </Stack>
            
            <Box sx={{ mt: 4, display: 'flex', justifyContent: 'center', gap: 3 }}>
              <Box sx={{ display: 'flex', alignItems: 'center' }}>
                <StarIcon sx={{ color: '#FFD700', mr: 0.5 }} />
                <Typography variant="body1">HD Quality</Typography>
              </Box>
              <Divider orientation="vertical" flexItem sx={{ bgcolor: 'rgba(255,255,255,0.3)' }} />
              <Typography variant="body1">Fast Downloads</Typography>
              <Divider orientation="vertical" flexItem sx={{ bgcolor: 'rgba(255,255,255,0.3)' }} />
              <Typography variant="body1">Multiple Servers</Typography>
            </Box>
          </Container>
        </Paper>

        {/* Trending Anime Section */}
        <Container maxWidth="xl" sx={{ py: 4 }}>
          <Box sx={{ display: 'flex', alignItems: 'center', mb: 3 }}>
            <WhatshotIcon sx={{ color: '#ffb6c1', mr: 1, fontSize: 30 }} />
            <Typography variant="h4" component="h2" gutterBottom sx={{ fontWeight: 'bold', mb: 0 }}>
              Trending Anime
            </Typography>
            <Box sx={{ flexGrow: 1 }} />
            <Button 
              variant="outlined" 
              component={RouterLink}
              to="/trending"
              sx={{ 
                borderColor: '#ffb6c1',
                color: '#ffb6c1',
                '&:hover': {
                  borderColor: '#ff99a8',
                  backgroundColor: 'rgba(255, 182, 193, 0.1)',
                }
              }}
            >
              View All
            </Button>
          </Box>
          
          {loading ? (
            <Box sx={{ display: 'flex', justifyContent: 'center', py: 5 }}>
              <CircularProgress sx={{ color: '#ffb6c1' }} />
            </Box>
          ) : (
            <Grid container spacing={3}>
              {trendingAnime.map(anime => renderAnimeCard(anime))}
            </Grid>
          )}
        </Container>

        {/* Top Rated Section */}
        <Box sx={{ bgcolor: '#1a1a1a', py: 4 }}>
          <Container maxWidth="xl">
            <Box sx={{ display: 'flex', alignItems: 'center', mb: 3 }}>
              <StarIcon sx={{ color: '#ffb6c1', mr: 1, fontSize: 30 }} />
              <Typography variant="h4" component="h2" gutterBottom sx={{ fontWeight: 'bold', mb: 0 }}>
                Top Rated Anime
              </Typography>
              <Box sx={{ flexGrow: 1 }} />
              <Button 
                variant="outlined" 
                component={RouterLink}
                to="/top-rated"
                sx={{ 
                  borderColor: '#ffb6c1',
                  color: '#ffb6c1',
                  '&:hover': {
                    borderColor: '#ff99a8',
                    backgroundColor: 'rgba(255, 182, 193, 0.1)',
                  }
                }}
              >
                View All
              </Button>
            </Box>
            
            {loading ? (
              <Box sx={{ display: 'flex', justifyContent: 'center', py: 5 }}>
                <CircularProgress sx={{ color: '#ffb6c1' }} />
              </Box>
            ) : (
              <Grid container spacing={3}>
                {popularAnime.map(anime => renderAnimeCard(anime))}
              </Grid>
            )}
          </Container>
        </Box>

        {/* Movies Section */}
        <Container maxWidth="xl" sx={{ py: 4 }}>
          <Box sx={{ display: 'flex', alignItems: 'center', mb: 3 }}>
            <MovieIcon sx={{ color: '#ffb6c1', mr: 1, fontSize: 30 }} />
            <Typography variant="h4" component="h2" gutterBottom sx={{ fontWeight: 'bold', mb: 0 }}>
              Anime Movies
            </Typography>
            <Box sx={{ flexGrow: 1 }} />
            <Button 
              variant="outlined" 
              component={RouterLink}
              to="/movies"
              sx={{ 
                borderColor: '#ffb6c1',
                color: '#ffb6c1',
                '&:hover': {
                  borderColor: '#ff99a8',
                  backgroundColor: 'rgba(255, 182, 193, 0.1)',
                }
              }}
            >
              View All
            </Button>
          </Box>
          
          {loading ? (
            <Box sx={{ display: 'flex', justifyContent: 'center', py: 5 }}>
              <CircularProgress sx={{ color: '#ffb6c1' }} />
            </Box>
          ) : (
            <Grid container spacing={3}>
              {popularAnime.map(anime => renderAnimeCard(anime))}
            </Grid>
          )}
        </Container>
      </Box>
    </>
  );
}

export default HomePage;
