import React, { useState } from 'react';
import { Link as RouterLink, useNavigate } from 'react-router-dom';
import {
  AppBar,
  Box,
  Toolbar,
  Typography,
  Button,
  IconButton,
  Drawer,
  List,
  ListItem,
  ListItemIcon,
  ListItemText,
  Divider,
  useMediaQuery,
  useTheme,
  Stack,
} from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';
import CloseIcon from '@mui/icons-material/Close';
import HomeIcon from '@mui/icons-material/Home';
import WhatshotIcon from '@mui/icons-material/Whatshot';
import MovieIcon from '@mui/icons-material/Movie';
import TvIcon from '@mui/icons-material/Tv';
import SearchIcon from '@mui/icons-material/Search';
import InfoIcon from '@mui/icons-material/Info';

const Header = () => {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));
  const navigate = useNavigate();
  
  const [drawerOpen, setDrawerOpen] = useState(false);
  
  const handleDrawerToggle = () => {
    setDrawerOpen(!drawerOpen);
  };
  
  const menuItems = [
    { text: 'Home', icon: <HomeIcon />, link: '/' },
    { text: 'Anime', icon: <MovieIcon />, link: '/anime' },
    { text: 'Movies', icon: <MovieIcon />, link: '/movies' },
    { text: 'TV Series', icon: <TvIcon />, link: '/series' },
    { text: 'Trending', icon: <WhatshotIcon />, link: '/trending' },
    { text: 'Search', icon: <SearchIcon />, link: '/search' },
    { text: 'About', icon: <InfoIcon />, link: '/about' },
  ];
  
  // Drawer content
  const drawerContent = (
    <Box sx={{ width: 280, bgcolor: '#1a1a1a', height: '100%' }}>
      <Box sx={{ py: 2, px: 3, display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
        <Typography variant="h6" component={RouterLink} to="/" sx={{ 
          color: 'white', 
          textDecoration: 'none',
          fontWeight: 'bold',
          display: 'flex',
          alignItems: 'center'
        }}>
          <Box component="span" sx={{ color: '#ffb6c1', mr: 0.5 }}>Anime</Box>
          Hindi4U
        </Typography>
        <IconButton onClick={handleDrawerToggle} sx={{ color: 'white' }}>
          <CloseIcon />
        </IconButton>
      </Box>
      
      <Divider sx={{ bgcolor: 'rgba(255,255,255,0.1)' }} />
      
      <List>
        {menuItems.map((item) => (
          <ListItem
            key={item.text}
            button
            component={RouterLink}
            to={item.link}
            onClick={handleDrawerToggle}
          >
            <ListItemIcon sx={{ color: '#ffb6c1', minWidth: 40 }}>
              {item.icon}
            </ListItemIcon>
            <ListItemText primary={item.text} />
          </ListItem>
        ))}
      </List>
    </Box>
  );

  return (
    <>
      <AppBar position="sticky" sx={{ bgcolor: '#121212', boxShadow: 3 }}>
        <Toolbar>
          <IconButton
            edge="start"
            color="inherit"
            aria-label="menu"
            onClick={handleDrawerToggle}
            sx={{ mr: 2, display: { sm: 'block', md: 'none' } }}
          >
            <MenuIcon />
          </IconButton>
          
          <Typography 
            variant="h6" 
            component={RouterLink} 
            to="/" 
            sx={{ 
              flexGrow: 1, 
              display: 'flex', 
              alignItems: 'center',
              color: 'white',
              textDecoration: 'none',
              fontWeight: 'bold',
            }}
          >
            <Box component="span" sx={{ color: '#ffb6c1', mr: 0.5 }}>
              Anime
            </Box>
            Hindi4U
          </Typography>
          
          {!isMobile && (
            <Stack direction="row" spacing={1} sx={{ mr: 2 }}>
              {menuItems.slice(0, 6).map((item) => (
                <Button 
                  key={item.text}
                  color="inherit" 
                  component={RouterLink} 
                  to={item.link}
                  sx={{ 
                    fontSize: '0.9rem',
                    '&:hover': { color: '#ffb6c1' }
                  }}
                >
                  {item.text}
                </Button>
              ))}
            </Stack>
          )}
          
          <IconButton
            component={RouterLink}
            to="/search"
            color="inherit"
            sx={{ ml: 1, display: { md: 'none' } }}
          >
            <SearchIcon />
          </IconButton>
        </Toolbar>
      </AppBar>
      
      <Drawer
        anchor="left"
        open={drawerOpen}
        onClose={handleDrawerToggle}
        PaperProps={{
          sx: {
            width: 280,
            bgcolor: '#1a1a1a',
            color: 'white',
          }
        }}
      >
        {drawerContent}
      </Drawer>
    </>
  );
};

export default Header;
