import React, { useState } from 'react';
import { Link as RouterLink, useNavigate } from 'react-router-dom';
import {
  AppBar,
  Box,
  Toolbar,
  Typography,
  Button,
  IconButton,
  Drawer,
  List,
  ListItem,
  ListItemIcon,
  ListItemText,
  Divider,
  Avatar,
  Menu,
  MenuItem,
  Badge,
  useMediaQuery,
  useTheme,
  Stack,
} from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';
import HomeIcon from '@mui/icons-material/Home';
import WhatshotIcon from '@mui/icons-material/Whatshot';
import MovieIcon from '@mui/icons-material/Movie';
import TvIcon from '@mui/icons-material/Tv';
import SearchIcon from '@mui/icons-material/Search';
import BookmarkIcon from '@mui/icons-material/Bookmark';
import HistoryIcon from '@mui/icons-material/History';
import SettingsIcon from '@mui/icons-material/Settings';
import LogoutIcon from '@mui/icons-material/Logout';
import PersonIcon from '@mui/icons-material/Person';
import NotificationsIcon from '@mui/icons-material/Notifications';

import AuthDialog from './AuthDialog';

const Header = () => {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));
  const navigate = useNavigate();
  
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [anchorEl, setAnchorEl] = useState(null);
  const [authDialog, setAuthDialog] = useState(false);
  
  // User authentication state
  const [user, setUser] = useState(null);
  
  const handleDrawerToggle = () => {
    setDrawerOpen(!drawerOpen);
  };
  
  const handleProfileMenu = (event) => {
    setAnchorEl(event.currentTarget);
  };
  
  const handleCloseMenu = () => {
    setAnchorEl(null);
  };
  
  const handleOpenAuthDialog = () => {
    setAuthDialog(true);
    setDrawerOpen(false);
  };
  
  const handleCloseAuthDialog = () => {
    setAuthDialog(false);
  };
  
  const handleLogin = (userData) => {
    // Save user data in state
    setUser(userData);
    // You could also store in localStorage for persistence
    localStorage.setItem('animeUser', JSON.stringify(userData));
  };
  
  const handleLogout = () => {
    setUser(null);
    localStorage.removeItem('animeUser');
    setAnchorEl(null);
    setDrawerOpen(false);
  };
  
  // For profile navigation
  const goToProfile = (section) => {
    navigate(`/profile/${section}`);
    setAnchorEl(null);
    setDrawerOpen(false);
  };
  
  // Drawer content
  const drawerContent = (
    <Box sx={{ width: 280, bgcolor: '#1a1a1a', height: '100%' }}>
      <Box sx={{ py: 2, px: 3, display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
        <Typography variant="h6" component={RouterLink} to="/" sx={{ 
          color: 'white', 
          textDecoration: 'none',
          fontWeight: 'bold',
          display: 'flex',
          alignItems: 'center'
        }}>
          <MovieIcon sx={{ mr: 1, color: '#ffb6c1' }} />
          AnimeStream
        </Typography>
      </Box>
      
      <Divider sx={{ bgcolor: 'rgba(255,255,255,0.1)' }} />
      
      {user ? (
        <Box sx={{ py: 2, px: 2, display: 'flex', alignItems: 'center' }}>
          <Avatar sx={{ bgcolor: '#ffb6c1', color: '#000' }}>
            {user.username.charAt(0).toUpperCase()}
          </Avatar>
          <Box sx={{ ml: 2 }}>
            <Typography variant="subtitle1" sx={{ fontWeight: 'bold' }}>
              {user.username}
            </Typography>
            <Typography variant="body2" color="text.secondary">
              {user.email}
            </Typography>
          </Box>
        </Box>
      ) : (
        <Box sx={{ py: 2, px: 2, display: 'flex', justifyContent: 'center' }}>
          <Button 
            variant="contained" 
            onClick={handleOpenAuthDialog}
            sx={{ 
              bgcolor: '#ffb6c1',
              color: '#000',
              fontWeight: 'bold',
              '&:hover': {
                bgcolor: '#ff99a8',
              }
            }}
          >
            Sign In
          </Button>
        </Box>
      )}
      
      <Divider sx={{ bgcolor: 'rgba(255,255,255,0.1)' }} />
      
      <List>
        <ListItem button component={RouterLink} to="/" onClick={() => setDrawerOpen(false)}>
          <ListItemIcon>
            <HomeIcon sx={{ color: 'white' }} />
          </ListItemIcon>
          <ListItemText primary="Home" />
        </ListItem>
        
        <ListItem button component={RouterLink} to="/trending" onClick={() => setDrawerOpen(false)}>
          <ListItemIcon>
            <WhatshotIcon sx={{ color: '#e53935' }} />
          </ListItemIcon>
          <ListItemText primary="Trending" />
        </ListItem>
        
        <ListItem button component={RouterLink} to="/movies" onClick={() => setDrawerOpen(false)}>
          <ListItemIcon>
            <MovieIcon sx={{ color: '#ffb6c1' }} />
          </ListItemIcon>
          <ListItemText primary="Movies" />
        </ListItem>
        
        <ListItem button component={RouterLink} to="/series" onClick={() => setDrawerOpen(false)}>
          <ListItemIcon>
            <TvIcon sx={{ color: '#3f51b5' }} />
          </ListItemIcon>
          <ListItemText primary="TV Series" />
        </ListItem>
        
        <ListItem button component={RouterLink} to="/anime" onClick={() => setDrawerOpen(false)}>
          <ListItemIcon>
            <SearchIcon sx={{ color: 'white' }} />
          </ListItemIcon>
          <ListItemText primary="Browse All" />
        </ListItem>
      </List>
      
      {user && (
        <>
          <Divider sx={{ bgcolor: 'rgba(255,255,255,0.1)' }} />
          
          <List>
            <ListItem button onClick={() => goToProfile('favorites')}>
              <ListItemIcon>
                <BookmarkIcon sx={{ color: '#ffc107' }} />
              </ListItemIcon>
              <ListItemText primary="My Favorites" />
            </ListItem>
            
            <ListItem button onClick={() => goToProfile('history')}>
              <ListItemIcon>
                <HistoryIcon sx={{ color: '#9c27b0' }} />
              </ListItemIcon>
              <ListItemText primary="Watch History" />
            </ListItem>
            
            <ListItem button onClick={() => goToProfile('settings')}>
              <ListItemIcon>
                <SettingsIcon sx={{ color: 'white' }} />
              </ListItemIcon>
              <ListItemText primary="Settings" />
            </ListItem>
            
            <ListItem button onClick={handleLogout}>
              <ListItemIcon>
                <LogoutIcon sx={{ color: '#f44336' }} />
              </ListItemIcon>
              <ListItemText primary="Logout" />
            </ListItem>
          </List>
        </>
      )}
    </Box>
  );

  return (
    <>
      <AppBar position="sticky" sx={{ bgcolor: '#121212', boxShadow: 3 }}>
        <Toolbar>
          <IconButton
            edge="start"
            color="inherit"
            aria-label="menu"
            onClick={handleDrawerToggle}
            sx={{ mr: 2 }}
          >
            <MenuIcon />
          </IconButton>
          
          <Typography 
            variant="h6" 
            component={RouterLink} 
            to="/" 
            sx={{ 
              flexGrow: 1, 
              display: 'flex', 
              alignItems: 'center',
              color: 'white',
              textDecoration: 'none',
              fontWeight: 'bold',
            }}
          >
            <MovieIcon sx={{ mr: 1, color: '#ffb6c1' }} />
            <Box component="span" sx={{ display: { xs: 'none', sm: 'inline' } }}>
              AnimeStream
            </Box>
          </Typography>
          
          {!isMobile && (
            <Stack direction="row" spacing={1} sx={{ mr: 2 }}>
              <Button 
                color="inherit" 
                component={RouterLink} 
                to="/trending"
                sx={{ 
                  fontSize: '0.9rem',
                  '&:hover': { color: '#ffb6c1' }
                }}
              >
                Trending
              </Button>
              <Button 
                color="inherit" 
                component={RouterLink} 
                to="/movies"
                sx={{ 
                  fontSize: '0.9rem',
                  '&:hover': { color: '#ffb6c1' }
                }}
              >
                Movies
              </Button>
              <Button 
                color="inherit" 
                component={RouterLink} 
                to="/series"
                sx={{ 
                  fontSize: '0.9rem',
                  '&:hover': { color: '#ffb6c1' }
                }}
              >
                Series
              </Button>
            </Stack>
          )}
          
          {user ? (
            <Box sx={{ display: 'flex', alignItems: 'center' }}>
              {!isMobile && (
                <IconButton color="inherit" sx={{ mr: 1 }}>
                  <Badge badgeContent={3} color="error">
                    <NotificationsIcon />
                  </Badge>
                </IconButton>
              )}
              
              <IconButton
                onClick={handleProfileMenu}
                size="small"
                edge="end"
                aria-label="account of current user"
                aria-haspopup="true"
              >
                <Avatar sx={{ bgcolor: '#ffb6c1', color: '#000', width: 32, height: 32 }}>
                  {user.username.charAt(0).toUpperCase()}
                </Avatar>
              </IconButton>
              
              <Menu
                anchorEl={anchorEl}
                open={Boolean(anchorEl)}
                onClose={handleCloseMenu}
                transformOrigin={{ horizontal: 'right', vertical: 'top' }}
                anchorOrigin={{ horizontal: 'right', vertical: 'bottom' }}
                PaperProps={{
                  sx: {
                    bgcolor: '#1a1a1a',
                    color: 'white',
                    mt: 1,
                    minWidth: 180,
                    boxShadow: '0 8px 16px rgba(0,0,0,0.5)',
                  }
                }}
              >
                <MenuItem onClick={() => goToProfile('account')} sx={{ gap: 1.5 }}>
                  <PersonIcon fontSize="small" />
                  Profile
                </MenuItem>
                <MenuItem onClick={() => goToProfile('favorites')} sx={{ gap: 1.5 }}>
                  <BookmarkIcon fontSize="small" />
                  My Favorites
                </MenuItem>
                <MenuItem onClick={() => goToProfile('history')} sx={{ gap: 1.5 }}>
                  <HistoryIcon fontSize="small" />
                  Watch History
                </MenuItem>
                <Divider sx={{ bgcolor: 'rgba(255,255,255,0.1)' }} />
                <MenuItem onClick={handleLogout} sx={{ gap: 1.5 }}>
                  <LogoutIcon fontSize="small" />
                  Logout
                </MenuItem>
              </Menu>
            </Box>
          ) : (
            <Button 
              variant="contained" 
              onClick={handleOpenAuthDialog}
              sx={{ 
                bgcolor: '#ffb6c1',
                color: '#000',
                fontWeight: 'bold',
                '&:hover': {
                  bgcolor: '#ff99a8',
                }
              }}
            >
              Sign In
            </Button>
          )}
        </Toolbar>
      </AppBar>
      
      <Drawer
        anchor="left"
        open={drawerOpen}
        onClose={handleDrawerToggle}
        ModalProps={{
          keepMounted: true, // Better mobile performance
        }}
        PaperProps={{
          sx: {
            bgcolor: '#1a1a1a',
            color: 'white',
          }
        }}
      >
        {drawerContent}
      </Drawer>
      
      <AuthDialog 
        open={authDialog} 
        onClose={handleCloseAuthDialog} 
        onLogin={handleLogin}
      />
    </>
  );
};

export default Header;
