import React, { useState, useEffect } from 'react';
import { Link as RouterLink } from 'react-router-dom';
import {
  Container,
  Typography,
  Grid,
  Box,
  Button,
  CircularProgress,
  TextField,
  InputAdornment,
} from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import StarIcon from '@mui/icons-material/Star';
import DownloadIcon from '@mui/icons-material/Download';
import animeData from '../data/animeData.json';

function MoviesPage() {
  const [movies, setMovies] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Load movie data from the comprehensive JSON file
    const loadMovieData = () => {
      try {
        setLoading(true);
        
        // Get only movies from the anime list
        const allAnime = animeData.animeList || [];
        const moviesList = allAnime.filter(anime => anime.type === 'Movie');
        
        // Sort by year (newest first)
        const sortedMovies = [...moviesList].sort((a, b) => b.year - a.year);
        setMovies(sortedMovies);
        
        setLoading(false);
      } catch (error) {
        console.error("Failed to load movie data:", error);
        setLoading(false);
      }
    };

    loadMovieData();
  }, []);

  const renderAnimeCard = (anime) => (
    <Grid item xs={6} sm={4} md={3} lg={2} key={anime.id}>
      <Box
        component={RouterLink}
        to={`/anime/${anime.id}`}
        sx={{
          textDecoration: 'none',
          color: 'inherit',
          display: 'block',
          transition: 'transform 0.3s ease',
          '&:hover': {
            transform: 'translateY(-5px)',
          },
        }}
      >
        <Box
          sx={{
            borderRadius: 2,
            overflow: 'hidden',
            position: 'relative',
            aspectRatio: '2/3',
            bgcolor: '#333',
            boxShadow: '0 4px 15px rgba(0,0,0,0.2)',
            mb: 1,
          }}
        >
          <Box
            component="img"
            src={anime.cover}
            alt={anime.title}
            sx={{
              width: '100%',
              height: '100%',
              objectFit: 'cover',
            }}
          />
          <Box
            sx={{
              position: 'absolute',
              top: 10,
              right: 10,
              bgcolor: 'rgba(0,0,0,0.7)',
              borderRadius: 1,
              px: 1,
              py: 0.5,
              display: 'flex',
              alignItems: 'center',
            }}
          >
            <StarIcon sx={{ color: '#FFD700', fontSize: 16, mr: 0.5 }} />
            <Typography variant="body2" sx={{ fontWeight: 'bold' }}>
              {anime.rating}
            </Typography>
          </Box>
          
          <Box
            sx={{
              position: 'absolute',
              bottom: 0,
              left: 0,
              right: 0,
              p: 1,
              background:
                'linear-gradient(to top, rgba(0,0,0,0.9) 0%, rgba(0,0,0,0.5) 70%, rgba(0,0,0,0) 100%)',
            }}
          >
            <Typography
              variant="caption"
              sx={{
                color: '#e91e63',
                fontWeight: 'bold',
                display: 'inline-block',
                bgcolor: 'rgba(0,0,0,0.5)',
                px: 0.5,
                py: 0.1,
                borderRadius: 0.5,
                mb: 0.5,
                fontSize: '0.6rem',
              }}
            >
              Movie
            </Typography>
            <Typography
              variant="subtitle2"
              component="h3"
              sx={{
                fontWeight: 'bold',
                display: '-webkit-box',
                overflow: 'hidden',
                textOverflow: 'ellipsis',
                WebkitLineClamp: 2,
                WebkitBoxOrient: 'vertical',
                fontSize: '0.75rem',
                mb: 0.5,
              }}
            >
              {anime.title}
            </Typography>
            <Button
              size="small"
              variant="contained"
              fullWidth
              startIcon={<DownloadIcon sx={{ fontSize: '0.75rem' }} />}
              sx={{
                bgcolor: '#4CAF50',
                color: 'white',
                py: 0.2,
                fontSize: '0.65rem',
                minHeight: 0,
                '&:hover': {
                  bgcolor: '#388E3C',
                },
              }}
            >
              Download
            </Button>
          </Box>
        </Box>
      </Box>
    </Grid>
  );

  return (
    <Box sx={{ bgcolor: '#121212', minHeight: '100vh', pt: 3, pb: 6 }}>
      <Container maxWidth="xl">
        <Box sx={{ mb: 4 }}>
          <Typography 
            variant="h4" 
            component="h1" 
            sx={{ 
              fontWeight: 'bold',
              mb: 1,
              color: 'white',
              textAlign: { xs: 'center', md: 'left' }
            }}
          >
            Anime Movies
          </Typography>
          <Typography 
            variant="body1" 
            color="text.secondary"
            sx={{ 
              mb: 2,
              textAlign: { xs: 'center', md: 'left' }
            }}
          >
            Complete collection of high-quality anime movies dubbed in Hindi
          </Typography>
        </Box>

        {loading ? (
          <Box sx={{ display: 'flex', justifyContent: 'center', py: 8 }}>
            <CircularProgress sx={{ color: '#ffb6c1' }} />
          </Box>
        ) : movies.length === 0 ? (
          <Typography variant="h6" sx={{ textAlign: 'center', my: 8, color: 'text.secondary' }}>
            No movies found
          </Typography>
        ) : (
          <Grid container spacing={3}>
            {movies.map(movie => renderAnimeCard(movie))}
          </Grid>
        )}
      </Container>
    </Box>
  );
}

export default MoviesPage;
