import React, { useState, useEffect } from 'react';
import { Link as RouterLink } from 'react-router-dom';
import {
  Container,
  Typography,
  Grid,
  Box,
  Button,
  CircularProgress,
  Tabs,
  Tab,
  Chip,
} from '@mui/material';
import StarIcon from '@mui/icons-material/Star';
import DownloadIcon from '@mui/icons-material/Download';
import animeData from '../data/animeData.json';

function TrendingPage() {
  const [trendingAnime, setTrendingAnime] = useState([]);
  const [loading, setLoading] = useState(true);
  const [tabValue, setTabValue] = useState(0);

  useEffect(() => {
    // Load trending anime data from the comprehensive JSON file
    const loadTrendingData = () => {
      try {
        setLoading(true);
        
        // Get all anime from the JSON data
        const allAnime = animeData.animeList || [];
        
        // Sort by views for most popular content
        const sortedByViews = [...allAnime].sort((a, b) => b.views - a.views);
        // Sort by rating for top rated content
        const sortedByRating = [...allAnime].sort((a, b) => b.rating - a.rating);
        // Sort by year for newest content
        const sortedByYear = [...allAnime].sort((a, b) => b.year - a.year);
        
        // Combine all sorted lists with most popular first
        setTrendingAnime({
          popular: sortedByViews.slice(0, 12),
          topRated: sortedByRating.slice(0, 12),
          newest: sortedByYear.slice(0, 12)
        });
        
        setLoading(false);
      } catch (error) {
        console.error("Failed to load trending anime data:", error);
        setLoading(false);
      }
    };

    loadTrendingData();
  }, []);

  const handleTabChange = (event, newValue) => {
    setTabValue(newValue);
  };

  const renderAnimeCard = (anime) => (
    <Grid item xs={6} sm={4} md={3} key={anime.id}>
      <Box
        component={RouterLink}
        to={`/anime/${anime.id}`}
        sx={{
          textDecoration: 'none',
          color: 'inherit',
          display: 'block',
          transition: 'transform 0.3s ease',
          '&:hover': {
            transform: 'translateY(-5px)',
          },
        }}
      >
        <Box
          sx={{
            borderRadius: 2,
            overflow: 'hidden',
            position: 'relative',
            aspectRatio: '2/3',
            bgcolor: '#333',
            boxShadow: '0 4px 15px rgba(0,0,0,0.2)',
            mb: 1,
          }}
        >
          <Box
            component="img"
            src={anime.cover}
            alt={anime.title}
            sx={{
              width: '100%',
              height: '100%',
              objectFit: 'cover',
              transition: 'transform 0.5s ease',
              '&:hover': {
                transform: 'scale(1.05)',
              },
            }}
          />
          <Box
            sx={{
              position: 'absolute',
              top: 10,
              right: 10,
              bgcolor: 'rgba(0,0,0,0.7)',
              borderRadius: 1,
              px: 1,
              py: 0.5,
              display: 'flex',
              alignItems: 'center',
            }}
          >
            <StarIcon sx={{ color: '#FFD700', fontSize: 16, mr: 0.5 }} />
            <Typography variant="body2" sx={{ fontWeight: 'bold' }}>
              {anime.rating}
            </Typography>
          </Box>
          
          {/* Type badge */}
          <Box
            sx={{
              position: 'absolute',
              top: 10,
              left: 10,
              bgcolor: anime.type === 'Movie' ? '#e91e63' : '#3f51b5',
              color: 'white',
              fontWeight: 'bold',
              fontSize: '0.7rem',
              borderRadius: 1,
              px: 0.8,
              py: 0.2,
            }}
          >
            {anime.type}
          </Box>
          
          <Box
            sx={{
              position: 'absolute',
              bottom: 0,
              left: 0,
              right: 0,
              p: 1.5,
              background:
                'linear-gradient(to top, rgba(0,0,0,0.9) 0%, rgba(0,0,0,0.5) 70%, rgba(0,0,0,0) 100%)',
            }}
          >
            <Box
              sx={{
                display: 'flex',
                justifyContent: 'space-between',
                alignItems: 'flex-end',
              }}
            >
              <Box>
                <Typography
                  variant="subtitle2"
                  component="h3"
                  sx={{
                    fontWeight: 'bold',
                    display: '-webkit-box',
                    overflow: 'hidden',
                    textOverflow: 'ellipsis',
                    WebkitLineClamp: 2,
                    WebkitBoxOrient: 'vertical',
                    fontSize: '0.85rem',
                    mb: 0.8,
                  }}
                >
                  {anime.title}
                </Typography>
              </Box>
            </Box>
            <Box sx={{ display: 'flex', alignItems: 'center', mt: 1, justifyContent: 'space-between' }}>
              <Button
                size="medium"
                variant="contained"
                startIcon={<DownloadIcon />}
                fullWidth
                sx={{
                  bgcolor: '#8957e5',
                  color: 'white',
                  py: 0.8,
                  fontSize: '0.8rem',
                  fontWeight: 'bold',
                  transition: 'all 0.3s ease',
                  '&:hover': {
                    bgcolor: '#7548c7',
                    transform: 'scale(1.05)',
                  },
                }}
              >
                {anime.type === 'Movie' ? 'Download Movie' : 'Download Episodes'}
              </Button>
              <Typography variant="caption" sx={{ color: 'rgba(255,255,255,0.7)', ml: 1 }}>
                {anime.year}
              </Typography>
            </Box>
          </Box>

          {/* Overlay details that appear on hover */}
          <Box
            sx={{
              position: 'absolute',
              top: 0,
              left: 0,
              right: 0,
              bottom: 0,
              bgcolor: 'rgba(0,0,0,0.7)',
              display: 'flex',
              flexDirection: 'column',
              justifyContent: 'center',
              alignItems: 'center',
              padding: 2,
              opacity: 0,
              transition: 'opacity 0.3s ease',
              '&:hover': {
                opacity: 1,
              },
            }}
          >
            <Typography 
              variant="body2" 
              sx={{ 
                color: 'white', 
                textAlign: 'center', 
                mb: 2,
                overflow: 'hidden',
                display: '-webkit-box',
                WebkitLineClamp: 3,
                WebkitBoxOrient: 'vertical',
              }}
            >
              {anime.description || 'No description available.'}
            </Typography>
            
            <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 0.5, justifyContent: 'center', mb: 2 }}>
              {anime.genres && anime.genres.slice(0, 3).map((genre, idx) => (
                <Chip 
                  key={idx} 
                  label={genre} 
                  size="small" 
                  sx={{ 
                    bgcolor: 'rgba(137, 87, 229, 0.3)',
                    color: 'white',
                    fontSize: '0.65rem',
                  }} 
                />
              ))}
            </Box>
            
            <Button
              size="medium"
              variant="contained"
              startIcon={<DownloadIcon />}
              fullWidth
              sx={{
                bgcolor: '#8957e5',
                color: 'white',
                py: 0.8,
                fontSize: '0.8rem',
                fontWeight: 'bold',
                transition: 'all 0.3s ease',
                '&:hover': {
                  bgcolor: '#7548c7',
                  transform: 'scale(1.05)',
                },
              }}
            >
              {anime.type === 'Movie' ? 'Download Movie' : 'Download Episodes'}
            </Button>
          </Box>
        </Box>
      </Box>
    </Grid>
  );

  return (
    <Box sx={{ bgcolor: '#121212', minHeight: '100vh', pt: 3, pb: 6 }}>
      <Container maxWidth="xl">
        <Box sx={{ mb: 4 }}>
          <Typography 
            variant="h4" 
            component="h1" 
            sx={{ 
              fontWeight: 'bold',
              mb: 1,
              color: 'white',
              textAlign: { xs: 'center', md: 'left' }
            }}
          >
            Trending Anime
          </Typography>
          <Typography 
            variant="body1" 
            color="text.secondary"
            sx={{ 
              mb: 2,
              textAlign: { xs: 'center', md: 'left' }
            }}
          >
            Discover the most popular and trending anime in Hindi
          </Typography>
        </Box>

        <Box sx={{ borderBottom: 1, borderColor: 'rgba(255, 255, 255, 0.1)', mb: 3 }}>
          <Tabs 
            value={tabValue} 
            onChange={handleTabChange}
            textColor="inherit"
            sx={{
              '& .MuiTab-root': {
                color: 'rgba(255,255,255,0.7)',
                '&.Mui-selected': {
                  color: '#ffb6c1',
                },
              },
              '& .MuiTabs-indicator': {
                backgroundColor: '#ffb6c1',
              },
            }}
          >
            <Tab label="Popular" />
            <Tab label="Top Rated" />
            <Tab label="Newest" />
          </Tabs>
        </Box>

        {loading ? (
          <Box sx={{ display: 'flex', justifyContent: 'center', py: 8 }}>
            <CircularProgress sx={{ color: '#ffb6c1' }} />
          </Box>
        ) : (
          <Box role="tabpanel" hidden={tabValue !== 0} id={`trending-tabpanel-0`}>
            {tabValue === 0 && trendingAnime.popular && (
              <Grid container spacing={3}>
                {trendingAnime.popular.map(anime => renderAnimeCard(anime))}
              </Grid>
            )}
          </Box>
        )}

        {!loading && (
          <Box role="tabpanel" hidden={tabValue !== 1} id={`trending-tabpanel-1`}>
            {tabValue === 1 && trendingAnime.topRated && (
              <Grid container spacing={3}>
                {trendingAnime.topRated.map(anime => renderAnimeCard(anime))}
              </Grid>
            )}
          </Box>
        )}

        {!loading && (
          <Box role="tabpanel" hidden={tabValue !== 2} id={`trending-tabpanel-2`}>
            {tabValue === 2 && trendingAnime.newest && (
              <Grid container spacing={3}>
                {trendingAnime.newest.map(anime => renderAnimeCard(anime))}
              </Grid>
            )}
          </Box>
        )}
      </Container>
    </Box>
  );
}

export default TrendingPage;
