import React, { useState } from 'react';
import { Link as RouterLink, useNavigate } from 'react-router-dom';
import {
  AppBar,
  Box,
  Toolbar,
  Typography,
  Button,
  IconButton,
  Drawer,
  List,
  ListItem,
  ListItemIcon,
  ListItemText,
  Divider,
  useMediaQuery,
  useTheme,
  Stack,
} from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';
import HomeIcon from '@mui/icons-material/Home';
import WhatshotIcon from '@mui/icons-material/Whatshot';
import MovieIcon from '@mui/icons-material/Movie';
import TvIcon from '@mui/icons-material/Tv';
import SearchIcon from '@mui/icons-material/Search';
import InfoIcon from '@mui/icons-material/Info';

const Header = () => {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));
  const navigate = useNavigate();
  
  const [drawerOpen, setDrawerOpen] = useState(false);
  
  const handleDrawerToggle = () => {
    setDrawerOpen(!drawerOpen);
  };
  
  // Drawer content
  const drawerContent = (
    <Box sx={{ width: 280, bgcolor: '#1a1a1a', height: '100%' }}>
      <Box sx={{ py: 2, px: 3, display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
        <Typography variant="h6" component={RouterLink} to="/" sx={{ 
          color: 'white', 
          textDecoration: 'none',
          fontWeight: 'bold',
          display: 'flex',
          alignItems: 'center'
        }}>
          <Box component="span" sx={{ color: '#ffb6c1', mr: 0.5 }}>Anime</Box>
          Hindi4U
        </Typography>
        <IconButton onClick={handleDrawerToggle} sx={{ color: 'white' }}>
          <MenuIcon />
        </IconButton>
      </Box>
      
      <Divider sx={{ bgcolor: 'rgba(255,255,255,0.1)' }} />
      
      <List>
        <ListItem button component={RouterLink} to="/" onClick={() => setDrawerOpen(false)}>
          <ListItemIcon>
            <HomeIcon sx={{ color: 'white' }} />
          </ListItemIcon>
          <ListItemText primary="Home" />
        </ListItem>
        
        <ListItem button component={RouterLink} to="/trending" onClick={() => setDrawerOpen(false)}>
          <ListItemIcon>
            <WhatshotIcon sx={{ color: '#e53935' }} />
          </ListItemIcon>
          <ListItemText primary="Trending" />
        </ListItem>
        
        <ListItem button component={RouterLink} to="/movies" onClick={() => setDrawerOpen(false)}>
          <ListItemIcon>
            <MovieIcon sx={{ color: '#ffb6c1' }} />
          </ListItemIcon>
          <ListItemText primary="Movies" />
        </ListItem>
        
        <ListItem button component={RouterLink} to="/series" onClick={() => setDrawerOpen(false)}>
          <ListItemIcon>
            <TvIcon sx={{ color: '#3f51b5' }} />
          </ListItemIcon>
          <ListItemText primary="TV Series" />
        </ListItem>
        
        <ListItem button component={RouterLink} to="/anime" onClick={() => setDrawerOpen(false)}>
          <ListItemIcon>
            <SearchIcon sx={{ color: 'white' }} />
          </ListItemIcon>
          <ListItemText primary="Browse All" />
        </ListItem>
        
        <ListItem button component={RouterLink} to="/about" onClick={() => setDrawerOpen(false)}>
          <ListItemIcon>
            <InfoIcon sx={{ color: '#4caf50' }} />
          </ListItemIcon>
          <ListItemText primary="About Us" />
        </ListItem>
      </List>
    </Box>
  );

  return (
    <AppBar position="sticky" sx={{ bgcolor: '#121212' }}>
      <Toolbar>
        <IconButton
          edge="start"
          color="inherit"
          aria-label="menu"
          onClick={handleDrawerToggle}
          sx={{ mr: 2 }}
        >
          <MenuIcon />
        </IconButton>
        
        <Typography
          variant="h6"
          component={RouterLink}
          to="/"
          sx={{
            flexGrow: 1,
            textDecoration: 'none',
            color: 'white',
            fontWeight: 'bold',
            display: 'flex',
            alignItems: 'center',
          }}
        >
          <Box
            component="span"
            sx={{
              color: '#ffb6c1',
              mr: 0.5,
            }}
          >
            Anime
          </Box>
          Hindi4U
        </Typography>
        
        {!isMobile && (
          <Stack direction="row" spacing={1}>
            <Button 
              color="inherit" 
              component={RouterLink} 
              to="/trending"
              sx={{ 
                fontSize: '0.9rem',
                '&:hover': { color: '#ffb6c1' }
              }}
            >
              Trending
            </Button>
            <Button 
              color="inherit" 
              component={RouterLink} 
              to="/movies"
              sx={{ 
                fontSize: '0.9rem',
                '&:hover': { color: '#ffb6c1' }
              }}
            >
              Movies
            </Button>
            <Button 
              color="inherit" 
              component={RouterLink} 
              to="/series"
              sx={{ 
                fontSize: '0.9rem',
                '&:hover': { color: '#ffb6c1' }
              }}
            >
              Series
            </Button>
            <Button 
              color="inherit" 
              component={RouterLink} 
              to="/about"
              sx={{ 
                fontSize: '0.9rem',
                '&:hover': { color: '#ffb6c1' }
              }}
            >
              About
            </Button>
          </Stack>
        )}
        
        <IconButton
          component={RouterLink}
          to="/search"
          color="inherit"
          aria-label="search"
          sx={{ ml: 1 }}
        >
          <SearchIcon />
        </IconButton>
      </Toolbar>
      
      <Drawer
        anchor="left"
        open={drawerOpen}
        onClose={handleDrawerToggle}
        PaperProps={{
          sx: {
            bgcolor: '#1a1a1a',
            color: 'white',
          }
        }}
      >
        {drawerContent}
      </Drawer>
    </AppBar>
  );
};

export default Header;
