import React, { useState, useEffect } from 'react';
import { Link as RouterLink } from 'react-router-dom';
import {
  Container,
  Typography,
  Grid,
  Box,
  Button,
  CircularProgress,
  TextField,
  InputAdornment,
  MenuItem,
  FormControl,
  Select,
  InputLabel,
  Divider,
  Chip,
} from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import FilterListIcon from '@mui/icons-material/FilterList';
import StarIcon from '@mui/icons-material/Star';
import DownloadIcon from '@mui/icons-material/Download';
import animeData from '../data/animeData.json';

function SeriesPage() {
  const [series, setSeries] = useState([]);
  const [filteredSeries, setFilteredSeries] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [filter, setFilter] = useState({
    status: 'all',
    genres: 'all',
    sort: 'latest'
  });

  useEffect(() => {
    // Load series data from the comprehensive JSON file
    const loadSeriesData = () => {
      try {
        setLoading(true);
        
        // Get only TV series from the anime list
        const allAnime = animeData.animeList || [];
        const seriesList = allAnime.filter(anime => anime.type === 'TV');
        
        setSeries(seriesList);
        setFilteredSeries(seriesList);
        
        setLoading(false);
      } catch (error) {
        console.error("Failed to load series data:", error);
        setLoading(false);
      }
    };

    loadSeriesData();
  }, []);

  // Extract all unique genres from series
  const allGenres = [...new Set(series.flatMap(s => s.genres || []))].sort();

  // Apply filters and search
  useEffect(() => {
    let result = [...series];
    
    // Apply search filter
    if (searchTerm) {
      const term = searchTerm.toLowerCase().trim();
      result = result.filter(anime => {
        if (anime.title.toLowerCase().includes(term)) return true;
        if (anime.altTitles && anime.altTitles.some(alt => alt.toLowerCase().includes(term))) return true;
        if (anime.genres && anime.genres.some(genre => genre.toLowerCase().includes(term))) return true;
        return false;
      });
    }
    
    // Apply status filter
    if (filter.status !== 'all') {
      result = result.filter(anime => anime.status === filter.status);
    }
    
    // Apply genre filter
    if (filter.genres !== 'all') {
      result = result.filter(anime => anime.genres && anime.genres.includes(filter.genres));
    }
    
    // Apply sort
    switch (filter.sort) {
      case 'latest':
        result.sort((a, b) => b.year - a.year);
        break;
      case 'oldest':
        result.sort((a, b) => a.year - b.year);
        break;
      case 'a-z':
        result.sort((a, b) => a.title.localeCompare(b.title));
        break;
      case 'z-a':
        result.sort((a, b) => b.title.localeCompare(a.title));
        break;
      case 'rating':
        result.sort((a, b) => b.rating - a.rating);
        break;
      case 'popular':
        result.sort((a, b) => b.views - a.views);
        break;
      default:
        break;
    }
    
    setFilteredSeries(result);
  }, [series, searchTerm, filter]);

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleFilterChange = (name) => (event) => {
    setFilter({
      ...filter,
      [name]: event.target.value
    });
  };

  const renderAnimeCard = (anime) => (
    <Grid item xs={6} sm={4} md={3} key={anime.id}>
      <Box
        component={RouterLink}
        to={`/anime/${anime.id}`}
        sx={{
          textDecoration: 'none',
          color: 'inherit',
          display: 'block',
          transition: 'transform 0.3s ease',
          '&:hover': {
            transform: 'translateY(-5px)',
          },
        }}
      >
        <Box
          sx={{
            borderRadius: 2,
            overflow: 'hidden',
            position: 'relative',
            aspectRatio: '2/3',
            bgcolor: '#333',
            boxShadow: '0 4px 15px rgba(0,0,0,0.2)',
            mb: 1,
          }}
        >
          <Box
            component="img"
            src={anime.cover}
            alt={anime.title}
            sx={{
              width: '100%',
              height: '100%',
              objectFit: 'cover',
              transition: 'transform 0.5s ease',
              '&:hover': {
                transform: 'scale(1.05)',
              },
            }}
          />
          <Box
            sx={{
              position: 'absolute',
              top: 10,
              right: 10,
              bgcolor: 'rgba(0,0,0,0.7)',
              borderRadius: 1,
              px: 1,
              py: 0.5,
              display: 'flex',
              alignItems: 'center',
              zIndex: 2,
            }}
          >
            <StarIcon sx={{ color: '#FFD700', fontSize: { xs: 14, sm: 16 }, mr: 0.5 }} />
            <Typography variant="body2" sx={{ fontWeight: 'bold', fontSize: { xs: '0.7rem', sm: '0.8rem' } }}>
              {anime.rating}
            </Typography>
          </Box>
          <Box
            sx={{
              position: 'absolute',
              top: 10,
              left: 10,
              bgcolor: '#3f51b5',
              color: 'white',
              fontWeight: 'bold',
              fontSize: { xs: '0.6rem', sm: '0.7rem' },
              borderRadius: 1,
              px: 0.8,
              py: 0.2,
              display: 'flex',
              alignItems: 'center',
              zIndex: 2,
            }}
          >
            {anime.episodes} Episodes
          </Box>
          <Box
            sx={{
              position: 'absolute',
              bottom: 0,
              left: 0,
              right: 0,
              p: { xs: 1, sm: 1.5 },
              background:
                'linear-gradient(to top, rgba(0,0,0,0.9) 0%, rgba(0,0,0,0.5) 70%, rgba(0,0,0,0) 100%)',
              zIndex: 1,
            }}
          >
            <Box
              sx={{
                display: 'flex',
                justifyContent: 'space-between',
                alignItems: 'flex-end',
              }}
            >
              <Box>
                <Typography
                  variant="caption"
                  sx={{
                    color: '#3f51b5',
                    fontWeight: 'bold',
                    display: 'inline-block',
                    bgcolor: 'rgba(0,0,0,0.5)',
                    px: 0.8,
                    py: 0.2,
                    borderRadius: 0.8,
                    mb: 0.8,
                    fontSize: { xs: '0.6rem', sm: '0.7rem' },
                  }}
                >
                  Series
                </Typography>
                <Typography
                  variant="subtitle2"
                  component="h3"
                  sx={{
                    fontWeight: 'bold',
                    display: '-webkit-box',
                    overflow: 'hidden',
                    textOverflow: 'ellipsis',
                    WebkitLineClamp: 2,
                    WebkitBoxOrient: 'vertical',
                    fontSize: { xs: '0.75rem', sm: '0.85rem' },
                    mb: 0.8,
                  }}
                >
                  {anime.title}
                </Typography>
              </Box>
            </Box>
            <Typography variant="caption" sx={{ color: 'rgba(255,255,255,0.7)', fontSize: { xs: '0.6rem', sm: '0.7rem' } }}>
              {anime.status}
            </Typography>
          </Box>

          {/* Overlay details that appear on hover over bottom half */}
          <Box
            sx={{
              position: 'absolute',
              top: '50%',
              left: 0,
              right: 0,
              bottom: 0,
              bgcolor: 'rgba(0,0,0,0.85)',
              display: 'flex',
              flexDirection: 'column',
              justifyContent: 'center',
              alignItems: 'center',
              padding: { xs: 1, sm: 2 },
              opacity: 0,
              transition: 'opacity 0.3s ease',
              '&:hover': {
                opacity: 1,
              },
              zIndex: 3,
            }}
          >
            <Typography 
              variant="subtitle2" 
              component="h3" 
              sx={{ 
                fontWeight: 'bold',
                color: 'white',
                mb: 0.5,
                textAlign: 'center',
                fontSize: { xs: '0.7rem', sm: '0.8rem' },
              }}
            >
              {anime.title}
            </Typography>
            
            <Typography 
              variant="body2" 
              sx={{ 
                color: 'white', 
                textAlign: 'center', 
                mb: { xs: 0.5, sm: 1 },
                overflow: 'hidden',
                display: '-webkit-box',
                WebkitLineClamp: 2,
                WebkitBoxOrient: 'vertical',
                fontSize: { xs: '0.65rem', sm: '0.75rem' },
                opacity: 0.9,
              }}
            >
              {anime.description || 'No description available.'}
            </Typography>
            
            <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 0.5, justifyContent: 'center', mb: { xs: 0.5, sm: 1 } }}>
              {anime.genres && anime.genres.slice(0, 3).map((genre, idx) => (
                <Chip 
                  key={idx} 
                  label={genre} 
                  size="small" 
                  sx={{ 
                    bgcolor: 'rgba(137, 87, 229, 0.3)',
                    color: 'white',
                    fontSize: { xs: '0.55rem', sm: '0.65rem' },
                    height: { xs: 16, sm: 20 },
                    '& .MuiChip-label': {
                      padding: { xs: '0 6px', sm: '0 8px' },
                    }
                  }} 
                />
              ))}
            </Box>

            <Box sx={{ display: 'flex', alignItems: 'center', gap: 0.5, mb: { xs: 0.5, sm: 1.5 }, flexWrap: 'wrap', justifyContent: 'center' }}>
              <Chip 
                label={`${anime.episodes} Ep`}
                size="small"
                sx={{ 
                  bgcolor: 'rgba(63, 81, 181, 0.5)',
                  color: 'white',
                  fontWeight: 'bold',
                  fontSize: { xs: '0.55rem', sm: '0.65rem' },
                  height: { xs: 16, sm: 20 },
                  '& .MuiChip-label': {
                    padding: { xs: '0 6px', sm: '0 8px' },
                  }
                }}
              />
              <Chip 
                label={anime.status}
                size="small"
                sx={{ 
                  bgcolor: anime.status === 'Completed' ? 'rgba(76, 175, 80, 0.5)' : 'rgba(255, 193, 7, 0.5)',
                  color: 'white',
                  fontWeight: 'bold',
                  fontSize: { xs: '0.55rem', sm: '0.65rem' },
                  height: { xs: 16, sm: 20 },
                  '& .MuiChip-label': {
                    padding: { xs: '0 6px', sm: '0 8px' },
                  }
                }}
              />
            </Box>
            
            <Button
              size="small"
              variant="contained"
              startIcon={<DownloadIcon sx={{ fontSize: { xs: 14, sm: 16 } }} />}
              fullWidth
              sx={{
                bgcolor: '#8957e5',
                color: 'white',
                py: { xs: 0.5, sm: 0.8 },
                fontSize: { xs: '0.7rem', sm: '0.8rem' },
                fontWeight: 'bold',
                transition: 'all 0.3s ease',
                '&:hover': {
                  bgcolor: '#7548c7',
                  transform: 'scale(1.05)',
                },
                mt: 'auto',
                minHeight: { xs: 24, sm: 32 },
              }}
            >
              Download Episodes
            </Button>
          </Box>
        </Box>
      </Box>
    </Grid>
  );

  return (
    <Box sx={{ bgcolor: '#121212', minHeight: '100vh', pt: 3, pb: 6 }}>
      <Container maxWidth="xl">
        <Box sx={{ mb: 4 }}>
          <Typography 
            variant="h4" 
            component="h1" 
            sx={{ 
              fontWeight: 'bold',
              mb: 1,
              color: 'white',
              textAlign: { xs: 'center', md: 'left' }
            }}
          >
            Anime TV Series
          </Typography>
          <Typography 
            variant="body1" 
            color="text.secondary"
            sx={{ 
              mb: 2,
              textAlign: { xs: 'center', md: 'left' }
            }}
          >
            Explore our collection of Hindi dubbed anime series
          </Typography>
          <Divider sx={{ borderColor: 'rgba(255, 255, 255, 0.1)' }} />
        </Box>

        <Box 
          sx={{ 
            display: 'flex', 
            flexDirection: { xs: 'column', md: 'row' }, 
            gap: 2, 
            mb: 4,
            alignItems: 'center'
          }}
        >
          <TextField
            placeholder="Search series..."
            value={searchTerm}
            onChange={handleSearchChange}
            variant="outlined"
            fullWidth
            sx={{
              flex: 2,
              '.MuiOutlinedInput-root': {
                bgcolor: 'rgba(255, 255, 255, 0.05)',
                borderRadius: 2,
                color: 'white',
                '&:hover .MuiOutlinedInput-notchedOutline': {
                  borderColor: 'rgba(255, 255, 255, 0.5)',
                },
                '&.Mui-focused .MuiOutlinedInput-notchedOutline': {
                  borderColor: '#ffb6c1',
                },
              }
            }}
            InputProps={{
              startAdornment: (
                <InputAdornment position="start">
                  <SearchIcon sx={{ color: 'rgba(255, 255, 255, 0.7)' }} />
                </InputAdornment>
              ),
            }}
          />
          
          <FormControl variant="outlined" sx={{ flex: 1, minWidth: 120 }}>
            <InputLabel id="status-label" sx={{ color: 'rgba(255, 255, 255, 0.7)' }}>Status</InputLabel>
            <Select
              labelId="status-label"
              value={filter.status}
              onChange={handleFilterChange('status')}
              label="Status"
              sx={{
                bgcolor: 'rgba(255, 255, 255, 0.05)',
                color: 'white',
                '.MuiOutlinedInput-notchedOutline': {
                  borderColor: 'rgba(255, 255, 255, 0.3)',
                },
                '&:hover .MuiOutlinedInput-notchedOutline': {
                  borderColor: 'rgba(255, 255, 255, 0.5)',
                },
                '&.Mui-focused .MuiOutlinedInput-notchedOutline': {
                  borderColor: '#ffb6c1',
                },
                '.MuiSvgIcon-root': {
                  color: 'white',
                }
              }}
            >
              <MenuItem value="all">All Status</MenuItem>
              <MenuItem value="Completed">Completed</MenuItem>
            </Select>
          </FormControl>
          
          <FormControl variant="outlined" sx={{ flex: 1, minWidth: 120 }}>
            <InputLabel id="genre-label" sx={{ color: 'rgba(255, 255, 255, 0.7)' }}>Genre</InputLabel>
            <Select
              labelId="genre-label"
              value={filter.genres}
              onChange={handleFilterChange('genres')}
              label="Genre"
              sx={{
                bgcolor: 'rgba(255, 255, 255, 0.05)',
                color: 'white',
                '.MuiOutlinedInput-notchedOutline': {
                  borderColor: 'rgba(255, 255, 255, 0.3)',
                },
                '&:hover .MuiOutlinedInput-notchedOutline': {
                  borderColor: 'rgba(255, 255, 255, 0.5)',
                },
                '&.Mui-focused .MuiOutlinedInput-notchedOutline': {
                  borderColor: '#ffb6c1',
                },
                '.MuiSvgIcon-root': {
                  color: 'white',
                }
              }}
            >
              <MenuItem value="all">All Genres</MenuItem>
              {allGenres.map(genre => (
                <MenuItem key={genre} value={genre}>{genre}</MenuItem>
              ))}
            </Select>
          </FormControl>
          
          <FormControl variant="outlined" sx={{ flex: 1, minWidth: 120 }}>
            <InputLabel id="sort-label" sx={{ color: 'rgba(255, 255, 255, 0.7)' }}>Sort By</InputLabel>
            <Select
              labelId="sort-label"
              value={filter.sort}
              onChange={handleFilterChange('sort')}
              label="Sort By"
              sx={{
                bgcolor: 'rgba(255, 255, 255, 0.05)',
                color: 'white',
                '.MuiOutlinedInput-notchedOutline': {
                  borderColor: 'rgba(255, 255, 255, 0.3)',
                },
                '&:hover .MuiOutlinedInput-notchedOutline': {
                  borderColor: 'rgba(255, 255, 255, 0.5)',
                },
                '&.Mui-focused .MuiOutlinedInput-notchedOutline': {
                  borderColor: '#ffb6c1',
                },
                '.MuiSvgIcon-root': {
                  color: 'white',
                }
              }}
            >
              <MenuItem value="latest">Latest</MenuItem>
              <MenuItem value="oldest">Oldest</MenuItem>
              <MenuItem value="a-z">A-Z</MenuItem>
              <MenuItem value="z-a">Z-A</MenuItem>
              <MenuItem value="rating">Highest Rated</MenuItem>
              <MenuItem value="popular">Most Popular</MenuItem>
            </Select>
          </FormControl>
        </Box>

        {loading ? (
          <Box sx={{ display: 'flex', justifyContent: 'center', py: 8 }}>
            <CircularProgress sx={{ color: '#ffb6c1' }} />
          </Box>
        ) : filteredSeries.length === 0 ? (
          <Typography variant="h6" sx={{ textAlign: 'center', my: 8, color: 'text.secondary' }}>
            No series found matching your criteria
          </Typography>
        ) : (
          <Grid container spacing={3}>
            {filteredSeries.map(series => renderAnimeCard(series))}
          </Grid>
        )}
      </Container>
    </Box>
  );
}

export default SeriesPage;
