import React, { useState, useEffect } from 'react';
import { Link as RouterLink } from 'react-router-dom';
import animeData from '../data/animeData.json';
import {
  Box,
  Container,
  Typography,
  Grid,
  Card,
  CardMedia,
  CardContent,
  CardActions,
  Button,
  Chip,
  CircularProgress,
  Divider,
} from '@mui/material';
import PlayArrowIcon from '@mui/icons-material/PlayArrow';
import DownloadIcon from '@mui/icons-material/Download';
import StarIcon from '@mui/icons-material/Star';

function MoviesPage() {
  const [movies, setMovies] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Load movie data from the comprehensive JSON file
    const loadMovieData = () => {
      try {
        setLoading(true);
        
        // Get only movies from the anime list
        const allAnime = animeData.animeList || [];
        const moviesList = allAnime.filter(anime => anime.type === 'Movie');
        
        // Sort by year (newest first)
        const sortedMovies = [...moviesList].sort((a, b) => b.year - a.year);
        setMovies(sortedMovies);
        
        setLoading(false);
      } catch (error) {
        console.error("Failed to load movie data:", error);
        setLoading(false);
      }
    };

    loadMovieData();
  }, []);

  const renderMovieCard = (movie) => (
    <Grid item xs={12} sm={6} md={4} lg={3} key={movie.id}>
      <Card 
        sx={{ 
          height: '100%', 
          display: 'flex', 
          flexDirection: 'column',
          bgcolor: 'rgba(255, 255, 255, 0.05)',
          transition: 'transform 0.3s ease-in-out',
          '&:hover': {
            transform: 'scale(1.03)',
            boxShadow: '0 8px 16px rgba(0,0,0,0.3)',
            bgcolor: 'rgba(255, 255, 255, 0.08)',
          }
        }}
      >
        <Box sx={{ position: 'relative' }}>
          <CardMedia
            component="img"
            height={250}
            image={movie.cover}
            alt={movie.title}
            sx={{ objectFit: 'cover' }}
          />
          <Box
            sx={{
              position: 'absolute',
              top: 10,
              right: 10,
              display: 'flex',
              alignItems: 'center',
              bgcolor: 'rgba(0, 0, 0, 0.7)',
              borderRadius: 1,
              px: 1,
              py: 0.5,
            }}
          >
            <StarIcon sx={{ color: '#FFD700', fontSize: 18, mr: 0.5 }} />
            <Typography variant="body2" sx={{ fontWeight: 'bold', color: 'white' }}>
              {movie.rating}
            </Typography>
          </Box>
          <Chip
            label="Movie"
            size="small"
            sx={{
              position: 'absolute',
              top: 10,
              left: 10,
              bgcolor: '#e53935',
              color: 'white',
              fontWeight: 'bold',
            }}
          />
          {movie.year && (
            <Chip
              label={movie.year}
              size="small"
              sx={{
                position: 'absolute',
                bottom: 10,
                left: 10,
                bgcolor: 'rgba(0, 0, 0, 0.7)',
                color: 'white',
              }}
            />
          )}
        </Box>
        
        <CardContent sx={{ flexGrow: 1 }}>
          <Typography variant="h6" component="div" sx={{ fontWeight: 'bold', mb: 1 }}>
            {movie.title}
          </Typography>
          
          <Box sx={{ display: 'flex', mb: 1, flexWrap: 'wrap', gap: 0.5 }}>
            {movie.genres && movie.genres.slice(0, 3).map((genre, index) => (
              <Chip 
                key={index} 
                label={genre} 
                size="small" 
                sx={{ 
                  bgcolor: 'rgba(255, 255, 255, 0.1)',
                  color: 'text.secondary',
                  fontSize: '0.75rem',
                }} 
              />
            ))}
          </Box>
          
          {movie.description && (
            <Typography variant="body2" color="text.secondary" sx={{ 
              mb: 1,
              display: '-webkit-box',
              WebkitLineClamp: 3,
              WebkitBoxOrient: 'vertical',
              overflow: 'hidden',
              textOverflow: 'ellipsis'
            }}>
              {movie.description}
            </Typography>
          )}
          
          <Typography variant="body2" color="text.secondary">
            Duration: {movie.duration || '90 mins'} â€¢ {movie.language || 'Hindi Dubbed'}
          </Typography>
        </CardContent>
        
        <CardActions sx={{ p: 2, pt: 0 }}>
          <Button
            component={RouterLink}
            to={`/anime/${movie.id}`}
            variant="contained"
            size="small"
            startIcon={<PlayArrowIcon />}
            sx={{ 
              bgcolor: '#ffb6c1',
              color: '#000',
              fontWeight: 'bold',
              '&:hover': {
                bgcolor: '#ff99a8',
              },
              flexGrow: 1
            }}
          >
            Watch Now
          </Button>
          
          <Button
            component={RouterLink}
            to={`/anime/${movie.id}`}
            variant="outlined"
            size="small"
            startIcon={<DownloadIcon />}
            sx={{ 
              borderColor: 'rgba(255, 255, 255, 0.3)',
              color: 'white',
              '&:hover': {
                borderColor: 'white',
                bgcolor: 'rgba(255, 255, 255, 0.1)',
              },
              ml: 1
            }}
          >
            Download
          </Button>
        </CardActions>
      </Card>
    </Grid>
  );

  return (
    <Box sx={{ bgcolor: '#121212', minHeight: '100vh', pt: 3, pb: 6 }}>
      <Container maxWidth="xl">
        <Box sx={{ mb: 4 }}>
          <Typography 
            variant="h4" 
            component="h1" 
            sx={{ 
              fontWeight: 'bold',
              mb: 1,
              color: 'white',
              textAlign: { xs: 'center', md: 'left' }
            }}
          >
            Anime Movies
          </Typography>
          <Typography 
            variant="body1" 
            color="text.secondary"
            sx={{ 
              mb: 2,
              textAlign: { xs: 'center', md: 'left' }
            }}
          >
            Complete collection of high-quality anime movies dubbed in Hindi
          </Typography>
          <Divider sx={{ borderColor: 'rgba(255, 255, 255, 0.1)' }} />
        </Box>

        {loading ? (
          <Box sx={{ display: 'flex', justifyContent: 'center', py: 8 }}>
            <CircularProgress sx={{ color: '#ffb6c1' }} />
          </Box>
        ) : movies.length === 0 ? (
          <Typography variant="h6" sx={{ textAlign: 'center', my: 8, color: 'text.secondary' }}>
            No movies found
          </Typography>
        ) : (
          <Grid container spacing={3}>
            {movies.map(movie => renderMovieCard(movie))}
          </Grid>
        )}
      </Container>
    </Box>
  );
}

export default MoviesPage;
