import React from 'react';
import {
  Box,
  Typography,
  Container,
  Grid,
  Card,
  CardMedia,
  CardContent,
  Divider,
  List,
  ListItem,
  ListItemText,
  Paper,
  Button,
  Link,
} from '@mui/material';
import TelegramIcon from '@mui/icons-material/Telegram';

function AboutPage() {
  // Character data
  const characters = [
    {
      name: 'Kousei Arima',
      role: 'Protagonist',
      description: 'A former piano prodigy who lost his ability to hear the piano after his mother\'s death.',
      image: 'https://m.media-amazon.com/images/M/MV5BYzM0ZjlkZTctY2VkYy00ZTY5LWI5ZjgtMDlmZDZiYzQzODYwXkEyXkFqcGdeQXVyNjMxNzQ2NTQ@._V1_.jpg',
    },
    {
      name: 'Kaori Miyazono',
      role: 'Deuteragonist',
      description: 'A free-spirited violinist who helps Kousei rediscover his love for music.',
      image: 'https://m.media-amazon.com/images/M/MV5BYzM0ZjlkZTctY2VkYy00ZTY5LWI5ZjgtMDlmZDZiYzQzODYwXkEyXkFqcGdeQXVyNjMxNzQ2NTQ@._V1_.jpg',
    },
    {
      name: 'Tsubaki Sawabe',
      role: 'Supporting Character',
      description: 'Kousei\'s childhood friend who has feelings for him.',
      image: 'https://m.media-amazon.com/images/M/MV5BYzM0ZjlkZTctY2VkYy00ZTY5LWI5ZjgtMDlmZDZiYzQzODYwXkEyXkFqcGdeQXVyNjMxNzQ2NTQ@._V1_.jpg',
    },
    {
      name: 'Ryota Watari',
      role: 'Supporting Character',
      description: 'Kousei\'s friend and a soccer player who Kaori initially pretends to like.',
      image: 'https://m.media-amazon.com/images/M/MV5BYzM0ZjlkZTctY2VkYy00ZTY5LWI5ZjgtMDlmZDZiYzQzODYwXkEyXkFqcGdeQXVyNjMxNzQ2NTQ@._V1_.jpg',
    },
  ];

  // Site features
  const features = [
    {
      title: "Hindi Dubbed Anime",
      description: "We provide high-quality Hindi dubbed anime series and movies for Indian anime fans.",
      image: "https://via.placeholder.com/300x200?text=Hindi+Dubbed",
    },
    {
      title: "Multiple Quality Options",
      description: "Download episodes in 1080p, 720p, or 480p quality based on your preference and internet speed.",
      image: "https://via.placeholder.com/300x200?text=Multiple+Quality",
    },
    {
      title: "Easy to Use",
      description: "Our website is designed to be user-friendly with a simple interface for easy navigation and downloads.",
      image: "https://via.placeholder.com/300x200?text=User+Friendly",
    },
    {
      title: "Regular Updates",
      description: "We regularly update our collection with new anime series and episodes in Hindi.",
      image: "https://via.placeholder.com/300x200?text=Regular+Updates",
    },
  ];

  return (
    <Container maxWidth="lg" sx={{ py: 4 }}>
      <Typography variant="h4" component="h1" gutterBottom align="center" sx={{ mb: 4, fontWeight: 'bold' }}>
        About AnimeHindiHub
      </Typography>

      {/* Hero Section */}
      <Paper
        sx={{
          position: 'relative',
          backgroundColor: 'grey.800',
          color: '#fff',
          mb: 6,
          backgroundSize: 'cover',
          backgroundRepeat: 'no-repeat',
          backgroundPosition: 'center',
          backgroundImage: `url(https://wallpaperaccess.com/full/3421252.jpg)`,
          height: '300px',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
        }}
      >
        <Box
          sx={{
            position: 'absolute',
            top: 0,
            bottom: 0,
            right: 0,
            left: 0,
            backgroundColor: 'rgba(0,0,0,.6)',
          }}
        />
        <Container maxWidth="md" sx={{ position: 'relative', textAlign: 'center' }}>
          <Typography component="h1" variant="h3" color="inherit" gutterBottom>
            AnimeHindiHub
          </Typography>
          <Typography variant="h5" color="inherit" paragraph>
            Your #1 Source for Hindi Dubbed Anime
          </Typography>
          <Button 
            variant="contained" 
            startIcon={<TelegramIcon />}
            component={Link}
            href="https://t.me/animehindihu"
            target="_blank"
            rel="noopener"
            sx={{ 
              mt: 2,
              bgcolor: '#0088cc',
              '&:hover': {
                bgcolor: '#006699',
              }
            }}
          >
            Join Our Telegram Channel
          </Button>
        </Container>
      </Paper>

      {/* About Us Section */}
      <Box sx={{ mb: 6 }}>
        <Typography variant="h5" component="h2" gutterBottom sx={{ fontWeight: 'bold' }}>
          About Us
        </Typography>
        <Typography paragraph>
          Welcome to AnimeHindiHub, your premier destination for watching and downloading anime in Hindi. We are passionate about bringing the best anime content to Hindi-speaking fans across India and worldwide.
        </Typography>
        <Typography paragraph>
          Our mission is to make anime accessible to everyone by providing high-quality Hindi dubs that maintain the essence and emotion of the original content. We understand the growing demand for localized anime content and strive to bridge the gap between global anime and Indian audiences.
        </Typography>
        <Typography paragraph>
          At AnimeHindiHub, we are committed to regularly updating our library with the latest anime series and movies in Hindi. Our platform offers a user-friendly interface, multiple quality options for downloads, and a seamless viewing experience.
        </Typography>
      </Box>

      {/* Features Section */}
      <Box sx={{ mb: 6 }}>
        <Typography variant="h5" component="h2" gutterBottom sx={{ mb: 3, fontWeight: 'bold' }}>
          What We Offer
        </Typography>
        <Grid container spacing={3}>
          {features.map((feature) => (
            <Grid item key={feature.title} xs={12} sm={6} md={3}>
              <Card
                sx={{
                  height: '100%',
                  display: 'flex',
                  flexDirection: 'column',
                  transition: '0.3s',
                  '&:hover': {
                    transform: 'translateY(-5px)',
                    boxShadow: '0 10px 20px rgba(0,0,0,0.2)',
                  },
                }}
              >
                <CardMedia
                  component="img"
                  height="140"
                  image={feature.image}
                  alt={feature.title}
                />
                <CardContent>
                  <Typography gutterBottom variant="h6" component="div" sx={{ fontWeight: 'bold' }}>
                    {feature.title}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    {feature.description}
                  </Typography>
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Box>

      {/* Connect With Us Section */}
      <Box sx={{ mb: 6 }}>
        <Typography variant="h5" component="h2" gutterBottom sx={{ fontWeight: 'bold' }}>
          Connect With Us
        </Typography>
        <Paper sx={{ p: 4, bgcolor: 'background.paper', textAlign: 'center' }}>
          <Typography variant="h6" gutterBottom>
            Join our Telegram community for the latest updates!
          </Typography>
          <Typography paragraph color="text.secondary">
            Get instant notifications about new releases, exclusive content, and engage with fellow anime enthusiasts.
          </Typography>
          <Button 
            variant="contained" 
            size="large"
            startIcon={<TelegramIcon />}
            component={Link}
            href="https://t.me/animehindihu"
            target="_blank"
            rel="noopener"
            sx={{ 
              mt: 2,
              bgcolor: '#0088cc',
              '&:hover': {
                bgcolor: '#006699',
              }
            }}
          >
            Follow Us on Telegram
          </Button>
        </Paper>
      </Box>

      {/* Disclaimer Section */}
      <Box sx={{ bgcolor: 'background.paper', p: 3, borderRadius: 1 }}>
        <Typography variant="h6" gutterBottom sx={{ fontWeight: 'bold' }}>
          Disclaimer
        </Typography>
        <Typography variant="body2" color="text.secondary">
          AnimeHindiHub does not store any files on our server. All content is provided by non-affiliated third parties. We just provide links available in the internet. The content hosted on third-party websites are not under our control, and we are not responsible for the compliance of the content with rules and regulations.
        </Typography>
        <Typography variant="body2" color="text.secondary" sx={{ mt: 1 }}>
          All anime titles, images, and content are copyrighted to their respective owners. We respect copyright laws and will respond to legitimate takedown requests.
        </Typography>
      </Box>
    </Container>
  );
}

export default AboutPage;
